import { BookingService } from './booking.service'
import { swiper, swiperSlide } from 'vue-awesome-swiper'
import 'swiper/dist/css/swiper.css'
import moment from 'moment'
import bookingTable from './components#/booking-table'
import memberSearch from '@/views/biz-components/member/member-search/member-search'
import { ruleOptions, columns } from './booking.config'
import SoldDealGatheringTip from '@/views/biz-modals/sold/deal/gathering-tip'
import SoldDealGathering from '@/views/biz-modals/sold/deal/gathering'
import SoldDealWaitPay from '@/views/biz-modals/brand/app/pos/wait-pay-result'
import { PatternService } from '@/services/pattern.service'
import { debounce } from 'lodash-es'
export default {
  name: 'PageShopAppVenueBooking',
  bem: {
    b: 'page-shop-app-venue-booking',
    calendar: 'calendar',
    list: 'list',
    right: 'right'
  },
  serviceInject() {
    return {
      bookingService: BookingService,
      pattern: PatternService
    }
  },
  rxState() {
    return {
      venueList: this.bookingService.venueList$,
      loading: this.bookingService.loading$,
      auth: this.bookingService.auth$,
      hasNext: this.bookingService.hasNext$
    }
  },
  modals: {
    SoldDealGatheringTip,
    SoldDealGathering,
    SoldDealWaitPay
  },
  components: {
    swiper,
    swiperSlide,
    bookingTable,
    memberSearch
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      sum: 0,
      finalAmount: 0,
      reduce_price: '',
      form,
      decorators,
      selectedList: [],
      columns,
      sliderOptions: {
        autoplay: false,
        navigation: {
          nextEl: '.swiper-booking-button-next',
          prevEl: '.swiper-booking-button-prev'
        },
        slidesPerView: 7,
        slidesPerGroup: 7,
        centeredSlides: false,
        normalizeSlideIndex: false
      },
      calendarData: [],
      query: {
        venues_id: '',
        page: 1,
        size: 10,
        reserve_day: ''
      },
      pickedIndex: 0,
      bookingList: [],
      siteX: [],
      siteY: []
    }
  },
  mounted() {
    this.calendarData = Array(28)
      .fill()
      .map((item, index) => {
        return {
          week: moment()
            .add(index, 'days')
            .format('ddd'),
          date: moment()
            .add(index, 'days')
            .format('YYYY-MM-DD'),
          day: moment()
            .add(index, 'days')
            .format('MM/DD')
        }
      })
    this.query.venues_id = this.venueList[0].venues_id
    this.pickDate(this.calendarData[0], 0)
  },
  watch: {
    reduce_price: debounce(function(val) {
      if (!this.selectedList.length) return
      this.calcPrice()
    }, 500)
  },
  methods: {
    onNextPage() {
      if (!this.hasNext) return
      this.query.page++
      this.bookingService.getBookingList(this.query).subscribe(res => {
        this.bookingList = this.bookingList.concat(res.list)
        this.siteX = this.siteX.concat(res.site_x)
        this.siteY = res.site_y
      })
    },
    resetPage() {
      this.form.resetFields()
      this.reduce_price = ''
      this.getList()
    },
    createdOrderPay(props) {
      return new Promise((resolve, reject) => {
        this.$modalRouter.push({
          name: 'sold-deal-gathering',
          props,
          on: {
            success: resolve,
            cancel: () => {
              this.resetPage()
            }
          }
        })
      })
      // this.$modalRouter.push({
      //   name: 'sold-deal-gathering',
      //   props,
      //   on: {
      //     success: res => {
      //       this.payCallBack(props.order_id, res.type, res)
      //     },
      //     cancel: () => {
      //       this.resetPage()
      //     }
      //   }
      // })
    },
    // 等待支付回调modal
    createdWaitPay(props) {
      return new Promise((resolve, reject) => {
        this.$modalRouter.push({
          name: 'sold-deal-wait-pay',
          props,
          on: {
            success: resolve
          }
        })
      })
    },
    payCallBack(orderId, callBackType, callBackData) {
      switch (callBackType) {
        case 'cancel':
          this.resetPage()
          break
        case 'pay':
          this.createdGatheringTip({
            message: '收款成功',
            order_id: orderId,
            needPay: false,
            needContract: false,
            needTicket: false
          })
          break
        case 'pos':
          this.createdWaitPay(callBackData).then(() => {
            this.createdGatheringTip({
              message: '收款成功',
              order_id: orderId
            })
          })
          break
      }
    },
    tipCallBack({ orderId, type }) {
      switch (type) {
        case 'cancel':
          this.resetPage()
          break
        case 'ViewOrder':
          this.createdOrderViewOrder(orderId)
          break
        case 'Pay':
          this.createdOrderPay({ order_id: orderId, type: 'venues' }).then(
            res => {
              this.payCallBack(orderId, res.type, res)
            }
          )
          break
      }
    },
    createdGatheringTip(props) {
      this.$modalRouter.push({
        name: 'sold-deal-gathering-tip',
        props,
        on: {
          success: this.tipCallBack
        }
      })
    },
    createdOrderViewOrder(order_id) {
      this.$router.push({
        name: 'shop-finance-order-info-collection-details',
        query: {
          id: order_id
        }
      })
    },
    createOrder() {
      return new Promise((resolve, reject) => {
        this.form.validate().then(values => {
          const venues_data = this.selectedList.map(item => {
            return {
              time_start: item.start_time,
              time_end: item.end_time,
              venues_site_id: +item.id.replace(/\-.*/, ''),
              price: item.price,
              venues_site_name: item.site_name
            }
          })
          values.mobile = values.mobile ? values.mobile.phone : undefined
          values.country_prefix = values.mobile
            ? values.mobile.code_id
            : undefined
          values.parent_mobile = values.parent_mobile
            ? values.parent_mobile.phone
            : undefined
          values.parent_country_prefix = values.parent_mobile
            ? values.parent_mobile.code_id
            : undefined
          this.bookingService
            .createOrder({
              venues_id: this.query.venues_id,
              venues_name: this.query.venues_name,
              reduce_price: this.reduce_price,
              actual_amount: this.finalAmount,
              order_amount: this.sum,
              reserve_day: this.query.reserve_day,
              venues_data: venues_data,
              ...values
            })
            .subscribe(result => {
              resolve(result)
            })
        })
      })
    },
    onCreateOrder() {
      this.createOrder().then(result => {
        const props = {
          order_id: result.info.order_id,
          message: '订单创建成功',
          needPay: true,
          needContract: false,
          needTicket: false
        }
        this.createdGatheringTip(props)
      })
    },
    onPay() {
      this.createOrder().then(result => {
        this.createdOrderPay({
          order_id: result.info.order_id,
          type: 'venues'
        }).then(res => {
          this.payCallBack(result.info.order_id, res.type, res)
        })
      })
    },
    deleteRow(row) {
      let index
      this.selectedList.forEach((item, inx) => {
        if (item.id === row.id) {
          index = inx
        }
      })
      this.$refs.bookingTable.deleteRow(index)
    },
    calcPrice() {
      const list = this.selectedList
      const venues_site_time = list.map(item => {
        return {
          start_time: item.start_time,
          end_time: item.end_time,
          site_id: +item.id.replace(/\-.*/, '')
        }
      })
      const params = {
        venues_id: this.query.venues_id,
        date: this.query.reserve_day,
        reduce_price: this.reduce_price || 0,
        venues_site_time
      }
      this.bookingService.calcPrice(params).subscribe(res => {
        this.sum = res.total_price
        this.finalAmount = res.price
      })
    },
    getSelectedList(list) {
      this.selectedList = list
      if (list.length === 0) {
        this.sum = 0
        this.finalAmount = 0
        return
      }
      this.calcPrice()
    },
    pickDate(dateObj, index) {
      this.calendarData.forEach(item => (item.ispick = false))
      this.pickedIndex = index
      this.query.reserve_day = dateObj.date
      this.resetPagination()
      this.getList()
    },
    selectHandler(e) {
      this.query.venues_id = e.target.value
      this.resetPagination()
      this.getList()
    },
    resetPagination() {
      this.query.page = 1
      this.$refs.bookingTable.resetScroll()
    },
    getList() {
      this.bookingService.getBookingList(this.query).subscribe(res => {
        this.$refs.bookingTable.deleteAll()
        this.bookingList = res.list
        this.siteX = res.site_x
        this.siteY = res.site_y
      })
    }
  }
}
