import CommonNotifyInfo from '@/views/biz-modals/common/notify/info'
import { InfoService } from './info.service'
export default {
  name: 'ModalNoticeSystem',
  bem: {
    b: 'modal-notice-system'
  },
  serviceInject() {
    return {
      infoService: InfoService
    }
  },
  data() {
    return {
      show: false
    }
  },
  modals: {
    CommonNotifyInfo
  },
  props: {
    info: {
      type: Object,
      default: () => {
        return {
          title: '还没',
          image_key: '',
          contnet: '还没'
        }
      }
    }
  },
  methods: {
    setRead() {
      this.show = false
      this.$emit('success')
    },
    onOk() {
      this.infoService
        .getAnnouncementInfo({
          id: this.info.announcement_id,
          notify_type: 1
        })
        .subscribe(res => {
          this.$modalRouter.push({
            name: 'common-notify-info',
            on: {
              cancel: () => {
                this.show = false
                this.setRead()
              }
            }
          })
        })
    },
    onCancel() {
      this.setRead()
    }
  }
}
