import { columns } from './team-consume.config'
import { TeamConsumeService } from './team-consume.service'
import { COURSE_TYPE } from '@/constants/stat/course'
import { cloneDeep } from 'lodash-es'
export default {
  name: 'ModalTeamConsume',
  serviceInject() {
    return {
      teamConsumeService: TeamConsumeService
    }
  },
  rxState() {
    const {
      consumeList$,
      page$,
      loading$,
      modalCoachList$,
      modalCourseList$,
      auth$
    } = this.teamConsumeService
    return {
      modalCoachList$,
      modalCourseList$,
      consumeList$,
      page$,
      loading$,
      auth$
    }
  },
  props: {
    record: {
      type: Object,
      default: () => {
        return {}
      }
    },
    type: String
  },
  data() {
    return {
      show: false,
      stat_date: '',
      consumeList: [],
      course_type: COURSE_TYPE.TEAM,
      coach_id: -1,
      course_id: -1,
      current_page: 1,
      // TODO: 后端翻页
      size: 99999
    }
  },
  computed: {
    columns,
    showTable() {
      return this.$route.path.includes('shop/course/summary') ? 'all' : 'coach'
    },
    exportParams() {
      const type = 'shop/team/course/checkin'
      return this.type === 'total'
        ? { type: `${type}/total`, query: this.totalQuery }
        : { type, query: this.query }
    },
    totalQuery() {
      let query = cloneDeep(this.$searchQuery)
      delete query.showTable
      delete query.current_page
      delete query.size
      query = {
        course_type: this.course_type,
        current_page: this.current_page,
        size: this.size,

        course_id: this.course_id,
        type: '/total',
        ...query
      }
      if (this.showTable) {
        query.coach_id = this.coach_id
      }
      return query
    },
    query() {
      return {
        stat_date: this.stat_date,
        coach_id: this.coach_id,
        course_id: this.course_id,
        current_page: this.current_page,
        size: this.size
      }
    }
  },
  methods: {
    getConsumeList() {
      const query = this.type === 'total' ? this.totalQuery : this.query
      this.teamConsumeService.getConsumeList(query).subscribe()
    },
    filterOption(input, option) {
      return (
        option.componentOptions.children[0].text
          .toLowerCase()
          .indexOf(input.toLowerCase()) >= 0
      )
    },
    init() {
      this.coach_id = this.record.coach_id || -1
      this.stat_date = this.record.stat_date
      const query = this.type === 'total' ? this.totalQuery : this.query
      this.teamConsumeService
        .init({ course_type: COURSE_TYPE.TEAM }, { ...query })
        .subscribe()
    }
  },
  mounted() {
    this.init()
  }
}
