import StTotal from '@/views/biz-components/st/total/total.vue'
import StTotalItem from '@/views/biz-components/st/total/total-item.vue'
import StatDetailListModal from '@/views/biz-modals/stat/order-detail'

import { GatherService } from './gather.service'
import tableMixin from '@/mixins/table.mixin'
import { columns, totalColumns } from './gather.config'
import moment from 'moment'

export default {
  name: 'ShopStatOrdersGather',
  mixins: [tableMixin],
  modals: {
    StatDetailListModal
  },
  serviceInject() {
    return {
      gatherService: GatherService
    }
  },
  rxState() {
    const {
      list$,
      transferType$,
      total$,
      rowSpanMap$,
      auth$,
      loading$
    } = this.gatherService
    return {
      list: list$,
      transferType: transferType$,
      total: total$,
      rowSpanMap: rowSpanMap$,
      auth: auth$,
      loading: loading$
    }
  },
  bem: {
    b: 'page-shop-stat-orders-gather',
    bHeader: 'page-brand-stat-header'
  },
  data() {
    return {}
  },
  computed: {
    columns,
    totalColumns
  },

  created() {},

  methods: {
    moment,
    onChangeDataDays(event) {
      this.$searchQuery.day = event.recently_day || undefined
      this.$searchQuery.start_date = event.start_date || undefined
      this.$searchQuery.end_date = event.end_date || undefined
      this.onSearch()
    },
    showOrdersModal(item, status, type) {
      const query =
        type === 'table'
          ? {
              product_type: item.product_type,
              transfer_type: item.transfer_type
            }
          : {}
      this.$modalRouter.push({
        name: 'stat-detail-list-modal',
        props: {
          query: {
            ...this.$$searchQuery,
            order_status: status,
            ...query
          },
          title: '订单明细',
          type: 'shop-orders-gather'
        }
      })
    }
  },
  components: {
    StTotal,
    StTotalItem
  }
}
