import { SelectCoachService } from './select-coach.service'
import CoachSelect from '@/views/biz-modals/coach/select'
export default {
  name: 'SelectCoach',
  serviceInject() {
    return {
      selectCoachService: SelectCoachService
    }
  },
  props: {
    coachIds: {
      type: Array,
      default() {
        return []
      }
    },
    shopIds: {
      type: Array,
      default() {
        return []
      }
    },
    type: {
      type: String,
      default: ''
    }
  },
  modals: {
    CoachSelect
  },
  data() {
    return {
      selected: [],
      list: []
    }
  },
  created() {
    this.selected = this.coachIds
    this.getSelectedList(this.coachIds)
  },
  methods: {
    onSelectComplete(coachIds) {
      this.getSelectedList(coachIds)
      this.selected = coachIds
      this.$emit('change', coachIds)
    },
    getSelectedList(coachIds = []) {
      if (coachIds.length) {
        this.selectCoachService
          .getCoachBasic({
            coach_ids: coachIds
          })
          .subscribe(res => {
            this.list = res.coaches
          })
      }
    },
    delTableRecord(id) {
      const { list, selected } = this
      list.forEach((item, index) => {
        if (+item.id === +id) {
          list.splice(index, 1)
        }
      })
      selected.forEach((item, index) => {
        if (+item === +id) {
          selected.splice(index, 1)
        }
      })
      this.list = list
      this.selected = selected
      this.$emit('change', selected)
    }
  }
}
