import { SelectShopService } from './select-shop.service'
import ShopSelect from '@/views/biz-modals/shop/select'
export default {
  name: 'SelectShop',
  modals: {
    ShopSelect
  },
  serviceInject() {
    return {
      selectService: SelectShopService
    }
  },
  rxState() {
    return {
      loading: this.selectService.loading$
    }
  },
  props: {
    shopIds: {
      type: Array,
      default() {
        return []
      }
    },
    disabled: {
      type: Boolean,
      default: false
    },
    groupParams: {
      type: Object,
      default: () => {
        return {}
      }
    },
    type: {
      type: String,
      default: ''
    }
  },
  data() {
    return {
      checkedShopIds: [],
      list: [],
      group: this.groupParams
    }
  },
  mounted() {
    this.checkedShopIds = this.shopIds
    this.getShops(this.shopIds)
  },
  computed: {
    colspanNum() {
      return this.disabled ? 4 : 5
    }
  },
  methods: {
    onSelectShopComplete(shopIds) {
      this.getShops(shopIds)
      this.checkedShopIds = shopIds
      this.$emit('change', shopIds)
    },
    getShops(shopIds = []) {
      if (!shopIds.length) {
        return
      }
      this.selectService
        .getShopBasic({
          shop_ids: shopIds
        })
        .subscribe(res => {
          this.list = res.shops
        })
    },
    delShopTableRecord(shopId) {
      const { list, checkedShopIds } = this
      list.forEach((item, index) => {
        if (item.shop_id === shopId) {
          list.splice(index, 1)
        }
      })
      checkedShopIds.forEach((item, index) => {
        if (item === +shopId) {
          checkedShopIds.splice(index, 1)
        }
      })
      console.log('list-shop', list)
      this.list = list
      this.checkedShopIds = checkedShopIds
      this.$emit('change', checkedShopIds)
    },
    // 拼团逻辑，清空门店
    clearShopList() {
      this.list = []
      this.checkedShopIds = []
      this.$emit('change', this.checkedShopIds)
    }
  }
}
