import moment from 'moment'
import { cloneDeep } from 'lodash-es'
import { ListService } from './list.service'
import { UserService } from '@/services/user.service'
import tableMixin from '@/mixins/table.mixin'
import { columns } from './list.config'
import Synchronize from '@/views/biz-modals/package/synchronize'
import { MessageService } from '@/services/message.service'
import { ruleOptions, ruleOffSaleOptions } from './list.config'

export default {
  name: 'ShopPackageList',
  mixins: [tableMixin],
  serviceInject() {
    return {
      userService: UserService,
      messageService: MessageService,
      listService: ListService
    }
  },
  modals: {
    Synchronize
  },
  rxState() {
    return {
      publicChannel$: this.listService.publicChannel$,
      supportNum$: this.listService.supportNum$,
      list: this.listService.list$,
      page: this.listService.page$,
      loading: this.listService.loading$,
      package_types: this.userService.getOptions$(
        'package_course.package_type'
      ),
      shelf_status: this.userService.getOptions$('package_course.shelf_status'),
      auth: this.listService.auth$
    }
  },
  bem: {
    listClass: 'page-shop-package-list'
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      form,
      decorators,
      offsaleIsShow: false,
      deleteIsShow: false,
      onsaleIsShow: false,
      packageId: '',
      packageName: '',
      start_time: null,
      end_time: null,
      endOpen: false,
      offSaleText: '',
      type: ['', 'unlimit', 'range', 'fix']
    }
  },
  computed: {
    columns
  },
  methods: {
    // 售卖时间-start
    start_time_change(data) {
      this.start_time = cloneDeep(data)
    },
    handleStartOpenChange(open) {
      if (!open) {
        this.endOpen = true
      }
    },
    disabledStartDate(startValue) {
      const endValue = this.end_time
      if (!endValue) {
        // 结束时间未选择
        return (
          startValue.valueOf() <
          moment()
            .subtract(1, 'd')
            .valueOf()
        )
      }
      let start =
        endValue.valueOf() >
        moment()
          .add(30, 'y')
          .valueOf()
          ? moment(endValue)
              .subtract(30, 'y')
              .valueOf()
          : moment()
              .subtract(1, 'd')
              .add(1, 'ms')
              .valueOf()
      return (
        startValue.valueOf() < start ||
        startValue.valueOf() >
          moment(endValue)
            .subtract(1, 'd')
            .valueOf()
      )
    },
    // 售卖时间-end
    end_time_change(data) {
      this.end_time = cloneDeep(data)
    },
    handleEndOpenChange(open) {
      this.endOpen = open
    },
    disabledEndDate(endValue) {
      const startValue = this.start_time
      if (!startValue) {
        // 开始时间未选择
        return (
          endValue.valueOf() >=
            moment()
              .add(30, 'y')
              .valueOf() || endValue.valueOf() <= moment().valueOf()
        )
      }
      return (
        endValue.valueOf() >=
          moment(startValue)
            .add(30, 'y')
            .valueOf() ||
        endValue.valueOf() <
          moment(startValue)
            .add(1, 'd')
            .valueOf()
      )
    },
    // moment
    moment,
    onAddPackage() {
      console.log('add')
    },
    onEdit(id, type) {
      this.$router.push({
        path: `/shop/product/course/manage/package/edit-${this.type[type]}-package`,
        query: { id }
      })
    },
    onDetail(id, type) {
      this.$router.push({
        path: `/shop/product/course/manage/package/info-${this.type[type]}-package`,
        query: { id }
      })
    },
    batchOperate(id) {
      this.$modalRouter.push({
        name: 'Synchronize',
        on: {
          success: () => {
            this.$confirm({
              title: '提示',
              content: `请再次确认是否同步，同步后会覆盖售出课程包，将会有2～5s无法正常使用`,
              onOk: () => {
                this.listService.setSyncSold(id).subscribe(res => {
                  this.$modalRouter.push({
                    name: 'success-tip'
                  })
                  this.$router.reload()
                })
              },
              onCancel() {}
            })
          }
        }
      })
    },
    onsalePackage(id, name, start, end) {
      if (
        end * 1000 >=
        moment(
          moment()
            .format()
            .replace(/\d{2}:\d{2}:\d{2}/, '23:59:59')
        ).valueOf()
      ) {
        this.listService
          .onsalePackage({
            id,
            start_time: `${this.moment(start * 1000).format(
              'YYYY-MM-DD'
            )} 00:00:00`,
            end_time: `${this.moment(end * 1000).format('YYYY-MM-DD')} 23:59:59`
          })
          .subscribe(res => {
            this.messageService.success({
              content: '上架成功'
            })
            this.$router.push({
              query: { ...this.$searchQuery, page: 1 }
            })
          })
      } else {
        this.onsaleIsShow = true
        this.packageId = id
        this.packageName = name
        this.$nextTick().then(res => {
          this.form.setFieldsValue({
            start_time: moment(start * 1000),
            end_time: moment(end * 1000)
          })
          this.form.validate(['start_time', 'end_time'])
        })
      }
    },
    onOnsale() {
      this.form.validate(['start_time', 'end_time']).then(res => {
        this.listService
          .onsalePackage({
            id: this.packageId,
            start_time: `${res.start_time.format('YYYY-MM-DD')} 00:00:00`,
            end_time: `${res.end_time.format('YYYY-MM-DD')} 23:59:59`
          })
          .subscribe(res => {
            this.onsaleIsShow = false
            this.packageId = ''
            this.packageName = ''
            this.messageService.success({
              content: '上架成功'
            })
            this.$router.push({
              query: { ...this.$searchQuery, page: 1 }
            })
          })
      })
    },
    offsalePackage(id, name) {
      this.offsaleIsShow = true
      this.packageId = id
      this.packageName = name
    },
    onOffsale() {
      this.form.validate().then(values => {
        this.listService
          .offsalePackage({ id: `${this.packageId}`, ...values })
          .subscribe(() => {
            this.offsaleIsShow = false
            this.packageId = ''
            this.packageName = ''
            this.form.setFieldsValue({
              reason: ''
            })
            this.messageService.success({
              content: '下架成功'
            })
            this.$router.push({ query: { ...this.$searchQuery, page: 1 } })
          })
      })
    },
    deletePackage(id, name) {
      this.deleteIsShow = true
      this.packageId = id
      this.packageName = name
    },
    onDelete() {
      this.listService.deletePackage(`${this.packageId}`).subscribe(() => {
        this.deleteIsShow = false
        this.packageId = ''
        this.packageName = ''
        this.onSearch()
      })
    }
  }
}
