import { ShopStatusService as EditService } from './status.service'
import { MessageService } from '@/services/message.service'
import { ruleOptions } from './status.config'

export default {
  serviceInject() {
    return {
      editService: EditService,
      messageService: MessageService
    }
  },
  rxState() {
    return {
      loading: this.editService.loading$,
      shopStatusList: this.editService.shopStatusList$,
      isValidList: this.editService.isValidList$
    }
  },
  props: {
    shopId: {
      type: [Number, String],
      default: 0
    },
    shopStatus: {
      type: Number,
      default: 0
    },
    shopName: {
      type: String,
      default: ''
    }
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      form,
      decorators,
      show: true
    }
  },
  mounted() {
    this.form.setFieldsValue({
      id: this.shopId,
      shop_status: +this.shopStatus
    })
  },
  methods: {
    onSubmit(e) {
      e.preventDefault()
      this.form.validate().then(values => {
        const data = this.form.getFieldsValue()
        /**
         * 修改门店运营状态为暂停营业（3），需二次确认
         */
        if (data.shop_status === 3) {
          this.$confirm({
            title: '',
            content:
              '门店暂停营业后，门店会员无法正常约课、上课、入离场；同时此门店不支持交易开单，请谨慎操作',
            onOk: () => {
              this.doSubmit(data)
            }
          })
        } else {
          this.doSubmit(data)
        }
      })
    },
    doSubmit(data) {
      this.editService.update(data).subscribe(this.onSubmitSuccess)
    },
    onSubmitSuccess() {
      this.messageService.success({
        content: '更改成功'
      })
      this.$emit('change')
      this.show = false
    },
    onCancel() {
      this.show = false
    }
  }
}
