import { FindService } from '../find.service'
import { ruleOptions } from './find.config'
import { PatternService } from '@/services/pattern.service'
import { stepArr } from './step.config'
import ValidId from './valid-id'
import ResetPwd from './reset-pwd'

export default {
  bem: {
    b: 'page-find-account-step'
  },
  name: 'FindAccountStep',
  serviceInject() {
    return {
      findService: FindService,
      pattern: PatternService
    }
  },
  rxState() {
    return {
      loading: this.findService.loading$
    }
  },
  props: {
    bindInfo: {
      type: Object
    }
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      form,
      decorators,
      stepArr,
      currentIndex: 0
    }
  },
  components: {
    ValidId,
    ResetPwd
  },
  methods: {
    changeStep(event) {
      this.currentIndex = event
    },
    onNext(params) {
      this.findService.checkCaptcha(params).subscribe(res => {
        this.currentIndex = 1
        this.$emit('step', 1)
      })
    },
    onReset(params) {
      params.account = this.bindInfo.account
      this.findService.repairPwd(params).subscribe(res => {
        this.currentIndex = 2
      })
    },
    goLogin() {
      localStorage.setItem('UserAccountForFind', this.bindInfo.account)
      this.$router.push({
        path: '/account/login'
      })
    }
  }
}
