import StaffInfo from './staff-info'
import { DeleteService } from './delete.service'
import StaffModalTips from '@/views/biz-components/staff/staff-modal-tips'
export default {
  name: 'DeleteStaff',
  serviceInject() {
    return {
      deleteService: DeleteService
    }
  },
  rxState() {
    return {
      list: this.deleteService.list$,
      operate: this.deleteService.operate$,
      loading: this.deleteService.loading$
    }
  },
  data() {
    return {
      show: false
    }
  },
  components: {
    StaffInfo,
    StaffModalTips
  },
  filters: {
    unitFilter(val) {
      return val === 5 ? '个' : '节'
    }
  },
  props: {
    staff: {
      type: Object,
      default: () => {}
    }
  },
  methods: {
    onSubmit() {
      if (!this.operate) {
      } else {
        this.deleteService
          .deleteStaffBrandInfo(this.staff.id + '')
          .subscribe(res => {
            this.$router.reload()
          })
      }
      this.show = false
    },
    isTurnover() {
      this.deleteService.getStaffCheckJob(this.staff.id + '').subscribe()
    }
  },
  mounted() {
    this.isTurnover()
  }
}
