import { MessageService } from '@/services/message.service'
import { SettingMinaMchService } from './mch.service'
export default {
  serviceInject() {
    return {
      messageService: MessageService,
      settingService: SettingMinaMchService
    }
  },
  props: {
    params: {
      type: Object,
      default: () => {}
    }
  },
  data() {
    return {
      show: false,
      info: {}
    }
  },
  created() {
    this.settingService.getInfo().subscribe(res => {
      this.info = res
    })
  },
  methods: {
    cancel() {
      this.show = false
    },
    save() {
      this.settingService.update({ ...this.info }).subscribe(() => {
        this.messageService.success({
          content: '提交成功'
        })
        this.show = false
        this.$emit('change')
      })
    },
    toSetMch() {
      this.show = false
      this.$router.push({
        path: '/brand/setting/mina/pay-config'
      })
    }
  }
}
