  methods: {
    // mock逻辑仅供参考
    onSearch(isReload, query) {
      if (query && query.coupon_name) this.keyword = query.coupon_name
      if (isReload) {
        this.current_page = 1
        this.isEnd = false
        this.total = Math.floor(Math.random() * 7) + 3
      } else {
        this.current_page += 1
      }
      this.isLoading = true
      if (this.current_page > this.total) return
      clearTimeout(this.timer)
      this.timer = setTimeout(() => {
        const list = []
        for (let i = 0, len = this.total * this.current_page; i < len; i++) {
          list.push({
            name: this.keyword + (i + 1),
            id: i + 1
          })
        }
        this.list = list
        this.isLoading = false
        if (this.current_page >= this.total) this.isEnd = true
      }, (Math.floor(Math.random() * 12) + 3) * 100)
    },
    onChange(e) {
      console.log('change', e)
    }
  }
