export default {
  name: 'SelectCondition',
  props: {
    value: {
      type: Array,
      default() {
        return []
      }
    }
  },
  data() {
    return {
      checked: []
    }
  },
  watch: {
    value(val) {
      this.setChecked()
    },
    checked(val) {
      this.$emit('change', val)
    }
  },
  created() {
    this.setChecked()
  },
  methods: {
    setChecked() {
      const value = this.value
      const checked = []
      value.forEach(item => {
        if (item.checked) {
          checked.push(item.id)
        }
      })
      this.checked = checked
    }
  }
}
