import { AreaService } from './area.service'
import { cloneDeep } from 'lodash-es'
export default {
  name: 'ModalSoldCardArea',
  props: {
    id: {
      type: Array,
      required: true
    }
  },
  serviceProviders() {
    return [AreaService]
  },
  serviceInject() {
    return {
      areaService: AreaService
    }
  },
  rxState() {
    return {
      vips: this.areaService.vips$,
      loading: this.areaService.loading$
    }
  },
  computed: {
    isOnlyOne() {
      return this.id.length === 1
    }
  },
  methods: {
    onSubmit() {
      this.areaService
        .setCardVip({
          id: this.id,
          vip_id: this.cardVip
        })
        .subscribe(res => {
          this.show = false
          this.$emit('success')
        })
    }
  },
  data() {
    return {
      show: false,
      cardVip: []
    }
  },
  created() {
    this.areaService.getVips().subscribe()
    if (this.isOnlyOne) {
      this.areaService.getCardVip(this.id[0]).subscribe(res => {
        res.list.forEach(o => {
          if (o.it_is === 1) {
            this.cardVip.push(o.id)
          }
        })
      })
    }
  }
}
