import { InputPhoneService } from './input-phone.service'
export default {
  name: 'InputPhone',
  bem: {
    b: 'input-phone'
  },
  serviceInject() {
    return {
      inputPhoneService: InputPhoneService
    }
  },
  rxState() {
    return {
      countryList: this.inputPhoneService.countryList$
    }
  },
  props: {
    placeholder: {
      type: String,
      default() {
        return ''
      }
    },
    disabled: {
      type: Boolean,
      default() {
        return false
      }
    },
    value: {
      type: Object
      // default: () => ({
      //   code_id: 37,
      //   phone_code: 86,
      //   phone: ''
      // })
    },
    size: {
      type: String,
      default: 'large'
    }
  },
  mounted() {
    this.inputPhoneService.getCountryCodes().subscribe()
  },
  methods: {
    onChange(event) {
      this.$emit('change', {
        phone: event.target.value,
        code_id: this.value.code_id,
        phone_code: this.value.phone_code
      })
    },
    onClickItem(event) {
      this.$emit('change', {
        phone: this.value.phone,
        phone_code: event.phone_code,
        code_id: event.code_id
      })
    }
  }
}
