import { MessageService } from '@/services/message.service'
import { AddTemporaryService as AddService } from './add-temporary.service'
import { ruleOptions } from './cabinet.config'

export default {
  serviceInject() {
    return {
      messageService: MessageService,
      addService: AddService
    }
  },
  rxState() {
    return {
      loading: this.addService.loading$,
      sortTypes: this.addService.sortTypes$
    }
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      form,
      decorators,
      show: false
    }
  },
  props: {
    id: {
      type: [Number, String],
      default: 0
    },
    areaName: {
      type: String,
      default: ''
    }
  },
  methods: {
    onSubmit(e) {
      e.preventDefault()
      this.form.validate().then(data => {
        data.cabinet_area_id = this.id
        this.addService.add(data).subscribe(this.onSubmitSuccess)
      })
    },
    onCancel() {
      this.show = false
    },
    onSubmitSuccess() {
      this.messageService.success({
        content: '保存成功'
      })
      this.show = false
      this.$emit('change')
    }
  }
}
