import { debounce, isEqual } from 'lodash-es'

export default {
  name: 'StSelectScroll',
  bem: {
    b: 'st-select-scroll'
  },
  model: {
    props: 'selected',
    event: 'change'
  },
  props: {
    value: {
      type: [String, Number, Number],
      default: undefined
    },
    list: {
      type: Array,
      default: () => []
    },
    labelHtml: {
      type: Function,
      default: (label, keyword) => {
        return label.replace(
          new RegExp(keyword, 'g'),
          `\<span class='global-highlight-color'\>${keyword}\<\/span\>`
        )
      }
    },
    loading: {
      type: Boolean,
      default: false
    },
    isSrcoll: {
      type: Boolean,
      default: false
    },
    isEnd: {
      type: Boolean,
      default: false
    },
    showSearch: {
      type: Boolean,
      default: true
    },
    showArrow: {
      type: Boolean,
      default: false
    },
    allowClear: {
      type: Boolean,
      default: false
    },
    _label: {
      type: String,
      default: 'label'
    },
    _value: {
      type: String,
      default: 'value'
    },
    // 搜索关键词字段名定义， 默认为 keyword， search refresh scroll 三个事件输出的参数为 {keyword: ''}
    _keyword: {
      type: String,
      default: 'keyword'
    },
    placeholder: String,
    // 联动条件
    query: {
      type: Object,
      default: () => {}
    }
  },
  data() {
    return {
      // 搜索关键字
      keyword: '',
      selected: undefined
    }
  },
  computed: {
    lists() {
      return this.list.map(item => ({
        label: item[this._label],
        value: item[this._value]
      }))
    },
    _allowClear() {
      return this.showSearch ? true : this.allowClear
    },
    isSearchNone() {
      return this.keyword !== '' && !this.list.length
    }
  },
  watch: {
    value: {
      handler(v) {
        this.selected = v
      },
      immediate: true
    },
    lists: {
      deep: true,
      handler(newValue, oldValue) {
        if (isEqual(newValue, oldValue) || !newValue.length) return
        // 默认选中第一项 blur时起作用
        this.selected = newValue[0].value
      }
    },
    // 联动条件刷新监听
    query: {
      deep: true,
      handler(newValue, oldValue) {
        if (isEqual(newValue, oldValue)) return
        this.keyword = ''
        this.$emit('refresh', { ...newValue, [this._keyword]: '' })
      }
    }
  },
  components: {},
  created() {
    this.selected = this.value
  },
  methods: {
    // 搜索事件触发
    onSearch: debounce(function(val) {
      console.log(val)
      let str = val.trim()
      if (this.keyword === str) return
      this.keyword = str
      if (str !== '') {
        this.$emit('search', { [this._keyword]: str })
      }
    }, 200),
    // 检测清空选中
    onChange(val) {
      !val && this.$emit('change', val)
    },
    // 选择发生变化
    onSelect(val) {
      this.$emit('change', val)
    },
    // 搜索框失焦事件
    onBlur() {
      this.$emit('blur', this.selected)
    },
    // 聚焦时先清空历史搜索
    onFocus() {
      this.keyword = ''
    },
    // 滚动到底部时触发事件
    scroll: debounce(function(e) {
      const { target } = e
      if (
        Math.floor(target.scrollTop) + target.clientHeight >
          target.scrollHeight - 20 &&
        this.isSrcoll
      ) {
        if (!this.loading && !this.isEnd) {
          this.$emit('scroll', { [this._keyword]: this.keyword })
        }
      }
    }, 200)
  }
}
