import moment from 'moment'
import { ReactiveService } from './reactive.service'
import BizSoldCourseInfo from '@/views/biz-components/sold/cource-info.vue'
import { ruleOptions } from './reactive.config'
import { get } from 'lodash-es'
export default {
  name: 'ModalSoldCourseReactive',
  bem: {
    reactive: 'modal-sold-course-reactive'
  },
  serviceInject() {
    return {
      reactiveService: ReactiveService
    }
  },
  rxState() {
    const { packageInfo$, loading$ } = this.reactiveService
    return { packageInfo$, loading$ }
  },
  props: ['id'],
  components: {
    BizSoldCourseInfo
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      form,
      decorators,
      show: false,
      addDate: 0
    }
  },
  computed: {
    expirationDate() {
      return moment()
        .add(this.addDate, 'days')
        .format('YYYY-MM-DD HH:mm')
    }
  },
  created() {
    this.reactiveService.getPackageInfo(this.id).subscribe()
  },
  methods: {
    moment,
    onChangeDate(value) {
      this.addDate = value
    },
    onSubmit() {
      this.form.validate().then(values => {
        values.extend_days = +values.extend_days
        this.reactiveService
          .setReactive({
            id: this.id,
            ...values
          })
          .subscribe(res => {
            this.$emit('success')
            this.show = false
          })
      })
    }
  }
}
