import { EditService } from '../edit.service'
import { MessageService } from '@/services/message.service'
import SelectCoach from '@/views/fragments/coach/select-coach'
import { RuleConfig } from '@/constants/course/rule'

export default {
  name: 'SetShopCoach',
  serviceInject() {
    return {
      editService: EditService,
      messageService: MessageService,
      ruleConfig: RuleConfig
    }
  },
  rxState() {
    return {
      loading: this.editService.loading$
    }
  },
  components: {
    SelectCoach
  },
  props: {
    info: {
      type: Object,
      default() {
        return {}
      }
    }
  },
  data() {
    return {
      form: this.$stForm.create()
    }
  },
  mounted() {
    this.$nextTick(() => {
      this.setFieldsValue()
    })
  },
  methods: {
    save(e) {
      e.preventDefault()
      this.form.validate().then(() => {
        const data = this.getData()
        this.editService.setShop(data).subscribe(() => {
          this.messageService.success({
            content: '提交成功'
          })
          this.$emit('goNext')
        })
      })
    },
    onSelectCoachChange(coachIds) {
      this.form.setFieldsValue({
        coach_ids: coachIds
      })
    },
    setFieldsValue() {
      const info = this.info
      this.form.setFieldsValue({
        course_name: info.course_name,
        coach_ids: info.coach_ids
      })
    },
    getData() {
      const data = this.form.getFieldsValue()
      data.course_id = +this.$searchQuery.id
      return data
    }
  }
}
