import { ShelfService } from './shelf.service'
import { UserService } from '@/services/user.service'
import { cloneDeep } from 'lodash-es'
import { PatternService } from '@/services/pattern.service'
import ShopHourPicker from '@/views/biz-components/shop-hour-picker/shop-hour-picker'
import { ruleOptions, shopColumns, admissionTimeList } from './shelf.config'
import { SHOP_MEMBER } from '@/constants/card/shop-member'
import { MessageService } from '@/services/message.service'
export default {
  name: 'ModalCardShopMemberShelf',
  bem: {
    shelves: 'modal-card-batch-shelves'
  },
  components: {
    ShopHourPicker
  },
  serviceInject() {
    return {
      pattern: PatternService,
      userService: UserService,
      messageService: MessageService,
      shelfService: ShelfService
    }
  },
  rxState() {
    return {
      course_interests: this.shelfService.course_interests$,
      activateTypes: this.shelfService.activateTypes$,
      shopName: this.userService.shop$,
      courseData: this.shelfService.courseList$,
      loading: this.shelfService.loading$,
      info: this.shelfService.info$,
      shelfCardDetail: this.shelfService.shelfCardDetail$
    }
  },
  props: {
    id: {
      type: Number,
      default: 0
    },
    actionType: {
      type: String,
      default: 'shelf'
    },
    title: {
      type: String,
      default: '标题'
    }
  },
  computed: {
    shopColumns,
    admissionTimeList,
    admissionTimeIsOk() {
      return this.admissionTimeText === ''
    },
    courseInterestsIsOk() {
      return this.courseInterestsStatus === 'success'
    },
    priceIsOk() {
      return this.priceHelpText === ''
    },
    priceValidataArray() {
      let array = []
      this.priceList.forEach(i => {
        array.push(i.priceInputValue)
      })
      return array
    }
  },
  watch: {
    priceList: {
      deep: true,
      handler() {
        let b = this.priceValidataArray.every(i =>
          this.pattern.NUM_FLOAT(1).test(i)
        )
        b && this.checkedPrice()
      }
    },
    info: {
      deep: true,
      handler(newVal) {
        newVal.specs.forEach(i => {
          this.priceList.push({
            ...i,
            priceInputValue: ''
          })
        })
      }
    }
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      SHOP_MEMBER,
      form,
      decorators,
      show: false,
      // 卡tag类型
      cardTypeTag: {
        1: 'number-card',
        2: 'period-card'
      },
      // 门店明细
      visible: false,
      // 范围价格列表
      priceList: [],
      priceHelpText: '',
      // 回传给后台的价格
      specs: [],
      // 开卡方式
      openTypeList: [],
      // 约课权益
      courseInterests: null,
      // 输入是否正确
      courseInterestsStatus: 'success',
      courseInterestsHelpText: '',
      // 课列表
      courseList: [],
      // 显示更多
      moreIsShow: true,
      admissionTime: 1,
      timeList: [],
      // 回传给后台的时间段
      inoutTime: [],
      admissionTimeText: '',
      // 选择的vip区域
      vipAreaList: []
    }
  },
  created() {
    const { actionType } = this
    this.shelfService.getInfo(this.id).subscribe(() => {
      if (actionType === 'updateShelf') {
        this.initShelfData()
      }
    })
  },
  methods: {
    getCourseList(search) {
      let query = { course_name: search }
      this.shelfService.courseListAction$.dispatch(query)
    },
    // 检验入场时间是否输入正确
    checkedAdmission() {
      this.admissionTimeText =
        this.admissionTime === this.SHOP_MEMBER.ADMISSION_TIME_2 &&
        this.moreIsShow &&
        !this.timeList.length
          ? '请选择入场时间'
          : ''
    },
    brandPriceSettingHandleChange({ value, key }) {
      this.priceList[key].priceInputValue = value
    },
    // 检验门店自主定价价格输入是否正确
    checkedPrice() {
      if (
        !(
          this.info.publish_channel === this.SHOP_MEMBER.PUBLISH_CHANNEL_1 &&
          this.info.price_setting === this.SHOP_MEMBER.PRICE_SETTING_2
        )
      ) {
        this.priceHelpText = ''
        return false
      }
      let b = this.priceValidataArray.every(i =>
        this.pattern.NUM_FLOAT(1).test(i)
      )
      this.priceHelpText = b ? '' : '请输入价格'
    },
    // 格式化价格
    formatSpecs() {
      this.specs = []
      if (
        this.info.publish_channel === this.SHOP_MEMBER.PUBLISH_CHANNEL_1 &&
        this.info.price_setting === this.SHOP_MEMBER.PRICE_SETTING_2
      ) {
        // 有价格范围
        this.priceList.forEach(i => {
          this.specs.push({
            specs_id: i.spec_id,
            price: i.priceInputValue
          })
        })
      } else {
        // 无价格范围,发布渠道为2是门店，一定是无范围的
        this.priceList.forEach(i => {
          this.specs.push({
            specs_id: i.spec_id,
            price: i.rally_price
          })
        })
      }
    },
    // 格式化入场时段
    formatWeek() {
      this.inoutTime = []
      this.timeList.forEach(i => {
        this.inoutTime.push({
          week_day: +i.week_day,
          time_duration: [{ start_time: i.start_time, end_time: i.end_time }]
        })
      })
    },
    // 去设置vip场地
    goSet() {
      this.show = false
      this.$router.push({
        path: '/shop/setting/court/list'
      })
    },
    onSubmit() {
      this.form.validate().then(values => {
        this.checkedAdmission()
        // TODO: shop hour picker后续修改，不能有默认值
        this.formatWeek()
        this.formatSpecs()
        if (!this.inputCheck()) {
          return
        }

        if (this.admissionTimeIsOk) {
          if (this.openTypeList.toString() === '3') {
            this.$confirm({
              title: '确认上架?',
              content:
                '当前仅选择指定日期开卡，上架后小程序不显示该卡，确认上架？',
              onOk: () => {
                this.sendShelfCard(values)
              }
            })
            return
          }
          this.sendShelfCard(values)
        }
      })
    },
    getParams(values) {
      const params = {
        areas: this.vipAreaList,
        open_type: this.openTypeList,
        activate_duration:
          values.openDay === undefined ? undefined : +values.openDay,
        course_interests: +this.courseInterests,
        courses: values.courseList,
        inout_type: this.admissionTime,
        inout_time: this.inoutTime,
        specs: this.specs
      }
      return params
    },
    inputCheck() {
      if (!this.courseInterests) {
        this.messageService.error({
          content: '请选择约课权益'
        })
        return false
      }
      return true
    },
    sendShelfCard(values) {
      const params = this.getParams(values)
      this.shelfService.shelfCard(params, this.id).subscribe(() => {
        this.show = false
        this.$emit('success')
      })
    },
    onUpdateShelf() {
      this.form.validate().then(values => {
        this.checkedAdmission()
        this.formatWeek()
        this.formatSpecs()
        const params = this.getParams(values)
        if (!this.inputCheck()) {
          return
        }
        this.shelfService.updateShelf(this.id, params).subscribe(() => {
          this.show = false
          this.$emit('success')
        })
      })
    },
    /**
     * 变更上架表单数据回显
     */
    initShelfData() {
      const detail = this.shelfCardDetail
      this.openTypeList = detail.activate_type
      this.courseInterests = detail.course_interests
      this.admissionTime = detail.inout_type
      this.timeList = this.timeFilter(detail.inout_time)
      this.vipAreaList = detail.areas
      this.$nextTick(() => {
        this.form.setFieldsValue({
          openDay: detail.automatic_num,
          courseList: detail.courses
        })
      })
      this.getCourseList()
    },
    timeFilter(timeList) {
      return timeList.map(item => {
        const { week_day } = item
        const { start_time, end_time } = item.time_duration[0]
        return {
          week_day,
          start_time,
          end_time
        }
      })
    }
  }
}
