import StTotal from '@/views/biz-components/st/total/total.vue'
import StTotalItem from '@/views/biz-components/st/total/total-item.vue'
import StatCashierDetailListModal from '@/views/biz-modals/stat/cashier-daily-detail'

import moment from 'moment'
import { IncomeService } from './income.service'
import tableMixin from '@/mixins/table.mixin'
import { columns, totalColumns } from './income.config'
export default {
  name: 'ShopStatCashierIncome',
  mixins: [tableMixin],
  modals: {
    StatCashierDetailListModal
  },
  serviceInject() {
    return {
      service: IncomeService
    }
  },
  rxState() {
    const { list$, total$, auth$, dataToday$, loading$ } = this.service
    return {
      list: list$,
      total: total$,
      auth: auth$,
      dataToday: dataToday$,
      loading: loading$
    }
  },
  bem: {
    b: 'page-shop-stat-cashier-income',
    bHeader: 'page-brand-stat-header'
  },
  data() {
    return {
      dataTodayShop: -1
    }
  },
  created() {
    this.getDataToday()
  },
  computed: {
    columns,
    dailyTotalColumns() {
      return totalColumns('daily')
    },
    totalColumns
  },
  methods: {
    moment,
    // TODO:
    onChangeDataDays(event) {
      this.$searchQuery.day = event.recently_day || undefined
      this.$searchQuery.start_date = event.start_date || undefined
      this.$searchQuery.end_date = event.end_date || undefined
      this.onSearch()
    },
    getDataToday() {
      return this.service
        .getDataToday({ shop_id: this.dataTodayShop })
        .subscribe()
    },
    refresh() {
      return this.service.getDataToday({
        shop_id: this.dataTodayShop,
        is_refresh: 1
      })
    }
  },
  components: {
    StTotal,
    StTotalItem
  }
}
