import { swiper, swiperSlide } from 'vue-awesome-swiper'
import { imgFilter } from '@/filters/resource.filters'

export default {
  name: 'h5PhoneShopinfoComponent',
  bem: {
    module1: 'h5-phone-shopinfo-component-1',
    module2: 'h5-phone-shopinfo-component-2'
  },
  components: {
    swiper,
    swiperSlide
  },
  props: {
    info: {
      type: Object,
      default: function() {
        return {}
      }
    },
    isActive: {
      type: Boolean,
      default: false
    },
    currentShopInfo: {
      type: Object,
      default: () => {
        return {
          shop_name: '',
          link_phone: ''
        }
      }
    }
  },
  watch: {
    infoList: {
      deep: true,
      handler(newValue, oldValue) {
        if (this.$refs.swiperContainer)
          this.$refs.swiperContainer.swiper.update()
      }
    }
  },
  computed: {
    infoList() {
      return this.info.list
    }
  },
  data() {
    return {
      sliderOptions: {
        autoplay: true,
        pagination: {
          el: '.swiper-pagination-shop',
          clickable: true
          // bulletClass: 'swiper-pagination-shop__item'
        },
        observer: true,
        observeParents: true
      },
      banner2Options: {
        spaceBetween: 6,
        slidesPerView: 2.4
      },
      img:
        'https://styd-saas-test.oss-cn-shanghai.aliyuncs.com/image/10000/2019-11-25/___75fc0ca429c8___.png'
    }
  },
  methods: {
    imgFilter
  }
}
