import CourseSelectCourse from '@/views/biz-modals/course/select-course'
import { FormService } from './form.service'
import { AddRangePackageService } from '../add-range-package.service'
import { forEach, cloneDeep, remove, filter, reduce } from 'lodash'
import { coachColumns } from '../../course/personal/info#table.config'
import { isEmpty } from 'lodash-es'
export default {
  name: 'AddCourseRangeTable',
  bem: {
    add: 'page-shop-add-range-package',
    basic: 'page-shop-add-basic-package'
  },
  serviceInject() {
    return {
      addRangeService: AddRangePackageService
    }
  },
  rxState() {
    return {
      coachList: this.addRangeService.coachList$
    }
  },
  computed: {
    // 团课小计
    team_total() {
      if (+this.packageData.team_times && +this.packageData.team_unit_price) {
        return (
          (this.packageData.team_times *
            (this.packageData.team_unit_price * 10)) /
          10
        )
      } else {
        return 0
      }
    },
    // 私教小计
    personal_total() {
      if (
        +this.packageData.personal_times &&
        +this.packageData.personal_unit_price
      ) {
        return (
          (this.packageData.personal_times *
            (this.packageData.personal_unit_price * 10)) /
          10
        )
      } else {
        return 0
      }
    },
    all_total() {
      let teamTotal = this.packageData.is_team ? this.team_total : 0
      let personalTotal = this.packageData.is_personal ? this.personal_total : 0
      return teamTotal + personalTotal
    }
  },
  watch: {
    packageData: {
      handler: function(newValue, oldValue) {
        if (!newValue.is_team && !newValue.is_personal) {
          this.$emit('change', {})
          return ''
        }
        if (newValue.is_team && !this.teamCourseList.length) {
          this.$emit('change', {})
          return false
        }
        if (newValue.is_personal && !this.personalCourseList.length) {
          this.$emit('change', {})
          return false
        }
        let personalTotal = 0
        let teamTotal = 0
        if (+newValue.personal_times && +newValue.personal_unit_price) {
          personalTotal =
            (newValue.personal_times * (newValue.personal_unit_price * 10)) / 10
        } else {
          personalTotal = 0
        }
        if (+newValue.team_times && +newValue.team_unit_price) {
          teamTotal =
            (newValue.team_times * (newValue.team_unit_price * 10)) / 10
        } else {
          teamTotal = 0
        }
        this.allTotal = personalTotal + teamTotal
        this.$emit(
          'change',
          this.getRangeCourse({
            team_range: this.teamCourseList,
            ...newValue,
            personal_range: this.personalCourseList
          })
        )
      },
      deep: true
    },
    teamCourseList: {
      handler: function(newValue, oldValue) {
        this.$emit(
          'change',
          this.getRangeCourse({
            team_range: newValue,
            ...this.packageData,
            personal_range: this.personalCourseList
          })
        )
      },
      deep: true
    },
    personalCourseList: {
      handler: function(newValue, oldValue) {
        this.$emit(
          'change',
          this.getRangeCourse({
            personal_range: newValue,
            ...this.packageData,
            team_range: this.teamCourseList
          })
        )
      },
      deep: true
    }
  },
  props: {
    useShopInfo: {
      type: Object,
      default: () => {}
    },
    initInfo: {
      type: Object,
      default: () => {
        return {}
      }
    },
    isLimit: {
      type: Number,
      default: 0
    }
  },
  data() {
    return {
      personalCourseTotalList: [],
      personalCoachListHistory: [],
      personalCoachTotalList: [],
      // 操作栏checkbox是否半选
      teamIndeterminate: false,
      // 操作栏checkbox是否半选
      personalIndeterminate: false,
      // 操作栏checkbox是全选
      personalCheckAll: false,
      // 配置课程表格是否显示
      teamCourseListIsShow: false,
      // 配置的课程总计
      teamCourseTotalObject: {
        team_times: 0,
        total: 0
      },
      // 备份课程已做的操作
      teamCourseTotalList: {},
      // 批量操作的值
      teamOperationObject: {
        team_times: undefined,
        team_unit_price: undefined
      },
      personalAllOperationCoachTotal: 0,
      // 操作栏选择教练等级的dorpdown是否显示
      coachAllOperationDropdownIsShow: false,
      // 操作栏教练等级选择的值
      personalAllOperationCoachList: [],
      // 批量操作的值
      personalOperationObject: {
        personal_times: undefined,
        personal_unit_price: undefined
      },
      // 配置课程表格是否显示
      personalCourseListIsShow: false,
      // 配置的课程总计
      personalCourseTotalObject: {
        personal_times: 0,
        total: 0
      },
      // team course
      teamCourseList: [],
      // personal course
      personalCourseList: [],
      // 课程范围是否未输入过
      courseIsFirstInput: true,
      // 操作栏checkbox是全选
      teamCheckAll: false,
      packageData: {
        is_team: false,
        is_personal: false,
        personal_times: undefined,
        team_times: undefined,
        team_unit_price: undefined,
        personal_unit_price: undefined
      },
      allTotal: 0
    }
  },
  modals: {
    CourseSelectCourse
  },
  filters: {
    // 单课价格统计
    course_item_total(times, unit_price) {
      if (+times && +unit_price) {
        return (+times * (+unit_price * 10)) / 10
      } else {
        return 0
      }
    }
  },
  methods: {
    getRangeCourse(rangeCourse) {
      const form = cloneDeep(rangeCourse)
      if (
        typeof form.personal_range === 'object' &&
        form.personal_range.length > 0
      ) {
        form.personal_range = form.personal_range.map(item => {
          const coach_level = item.coachGradeList.map(item =>
            typeof item !== 'object' ? item : item.id
          )
          return {
            course_id: item.course_id,
            coach_level
          }
        })
      }
      if (typeof form.team_range === 'object') {
        form.team_range = form.team_range.map(item => {
          return item.course_id
        })
      }
      if (!rangeCourse.is_team) {
        form.team_range = []
      }
      if (!rangeCourse.is_personal) {
        form.personal_range = []
      }
      form.is_team = +rangeCourse.is_team
      form.is_personal = +rangeCourse.is_personal
      return form
    },
    coachAllChange(e) {
      let selectCoach = filter(this.coachList, o => e.includes(o.id))
      this.personalAllOperationCoachTotal = reduce(
        selectCoach,
        (sum, o) => sum + o.coach_number,
        0
      )
    },
    // 移除选择的课程
    removeCourseItems(type) {
      if (type === 'team') {
        let arr = cloneDeep(this.teamCourseList)
        remove(arr, o => o.courseChecked)
        this.teamCourseList = arr
      } else if (type === 'personal') {
        let arr = cloneDeep(this.personalCourseList)
        let removeList = remove(arr, o => o.courseChecked)
        this.personalCourseList = arr
        // 移除备份选择的教练list
        let coachList = cloneDeep(this.personalCoachTotalList)
        removeList.forEach(i => {
          delete coachList[i.course_id]
        })
        this.personalCoachTotalList = coachList
      }
    },
    // item coach checkboxGroup changeEvent
    coachItemChange(e, id, index) {
      if (!e.length) {
        this.personalCourseList[
          index
        ].coachGradeList = this.personalCoachListHistory[index]
        return
      } else {
        this.personalCoachListHistory[index] = cloneDeep(e)
      }
      console.log(this.personalCourseList)
      this.personalCoachTotalList[id] = {}
      this.personalCoachTotalList[id].list = cloneDeep(e)
      let selectCoach = filter(this.coachList, o => e.includes(o.id))
      let total = reduce(selectCoach, (sum, o) => sum + o.coach_number, 0)
      this.personalCoachTotalList[id].total = total
      // 设置教练总数
      this.personalCourseList.forEach(i => {
        if (i.course_id === id) {
          i.coach = total
        }
      })
    },
    // 选择课程
    courseItemCheckedChange(e, type, index) {
      if (type === 'team') {
        this.teamCourseList[index].courseChecked = e.target.checked
      } else if (type === 'personal') {
        this.personalCourseList[index].courseChecked = e.target.checked
      }
    },
    // teamItems input change
    teamInputChange(e, type, id) {
      this.teamCourseTotalList[id] = this.teamCourseTotalList[id]
        ? this.teamCourseTotalList[id]
        : {}
      this.teamCourseTotalList[id][type] = e
    },
    // 移除课程
    removeCourseItem(type, id) {
      if (type === 'team') {
        let arr = cloneDeep(this.teamCourseList)
        remove(arr, o => o.course_id === id)
        this.teamCourseList = arr
        // 移除备份
        let list = cloneDeep(this.teamCourseTotalList)
        delete list[id]
        this.teamCourseTotalList = list
      } else if (type === 'personal') {
        let arr = cloneDeep(this.personalCourseList)
        remove(arr, o => o.course_id === id)
        this.personalCourseList = arr
        // 移除备份
        let coachList = cloneDeep(this.personalCourseTotalList)
        delete coachList[id]
        this.personalCourseTotalList = coachList
      }
    },
    // 新增团课课程
    addCourseTeam() {
      this.$modalRouter.push({
        name: 'course-select-course',
        props: {
          platform: 'brand',
          type: 'team',
          useShopInfo: this.useShopInfo,
          courses: this.teamCourseList
        },
        on: {
          ok: data => {
            data.list.forEach(i => {
              i.courseChecked = false
              i.team_times = null
              i.team_unit_price = null
            })
            forEach(this.teamCourseTotalList, (value, key) => {
              forEach(data.list, innerValue => {
                if (+innerValue.course_id === +key) {
                  innerValue.team_times = value.team_times
                  innerValue.team_unit_price = value.team_unit_price
                }
              })
              // 删除teamCourseTotalList里不存在的course_id项
              if (!data.list.some(o => +o.course_id === +key)) {
                delete this.teamCourseTotalList[key]
              }
            })
            this.teamCourseList = cloneDeep(data.list)
          }
        }
      })
    },
    // 新增私教课
    addCoursePersonal() {
      let that = this
      this.$modalRouter.push({
        name: 'course-select-course',
        props: {
          platform: 'brand',
          type: 'personal',
          useShopInfo: this.useShopInfo,
          courses: this.personalCourseList
        },
        on: {
          ok(data) {
            let coachGradeList = []
            forEach(that.coachList, i => {
              coachGradeList.push(i.id)
            })
            let total = reduce(
              that.coachList,
              (sum, o) => sum + o.coach_number,
              0
            )
            data.list.forEach(i => {
              i.courseChecked = false
              i.coachGradeList = coachGradeList
              i.coach = total
            })
            forEach(that.personalCoachTotalList, (value, key) => {
              forEach(data.list, innerValue => {
                if (+innerValue.course_id === +key) {
                  innerValue.coachGradeList = cloneDeep(value.list)
                  innerValue.coach = cloneDeep(value.total)
                }
              })
              // 删除personalCoachTotalList里不存在的course_id项
              if (!data.list.some(o => +o.course_id === +key)) {
                delete that.personalCoachTotalList[key]
              }
            })
            that.personalCoachListHistory = []
            // 缓存选择的教练等级
            data.list.forEach(i => {
              that.personalCoachListHistory.push(i.coachGradeList)
            })
            that.personalCourseList = cloneDeep(data.list)
          }
        }
      })
    },
    teamCheckboxChange(event) {
      this.courseIsFirstInput = false
      this.teamCourseListIsShow = +event.target.checked
    },
    personalCheckboxChange(e) {
      this.courseIsFirstInput = false
      this.personalCourseListIsShow = +e.target.checked
    },
    // team
    teamCourseListShow() {
      this.teamCourseListIsShow = !this.teamCourseListIsShow
    },
    // personal
    personalCourseListShow() {
      this.personalCourseListIsShow = !this.personalCourseListIsShow
    },
    // 全选
    checkAllChange(type) {
      if (type === 'team') {
        this.teamCheckAll = !this.teamCheckAll
        this.teamCourseList.forEach(i => {
          i.courseChecked = this.teamCheckAll
        })
      } else if (type === 'personal') {
        this.personalCheckAll = !this.personalCheckAll
        this.personalCourseList.forEach(i => {
          i.courseChecked = this.personalCheckAll
        })
      }
    },
    // 批量操作团课课程包节数
    timesOperationOk(type) {
      if (type === 'team') {
        forEach(this.teamCourseList, o => {
          if (o.courseChecked) {
            o.team_times = +this.teamOperationObject.team_times
            // 备份批量操作的数据
            this.teamCourseTotalList[o.course_id] = this.teamCourseTotalList[
              o.course_id
            ]
              ? this.teamCourseTotalList[o.course_id]
              : {}
            this.teamCourseTotalList[o.course_id].team_times = +this
              .teamOperationObject.team_times
          }
        })
      } else if (type === 'personal') {
        forEach(this.personalCourseList, o => {
          if (o.courseChecked) {
            o.personal_times = +this.personalOperationObject.personal_times
            // 备份批量操作的数据
            this.personalCourseTotalList[o.course_id] = this
              .personalCourseTotalList[o.course_id]
              ? this.personalCourseTotalList[o.course_id]
              : {}
            this.personalCourseTotalList[o.course_id].personal_times = +this
              .personalOperationObject.personal_times
          }
        })
      }
    },
    // 批量操作团课课程包价格
    unitPriceOperationOk(type) {
      if (type === 'team') {
        forEach(this.teamCourseList, o => {
          if (o.courseChecked) {
            o.team_unit_price = +this.teamOperationObject.team_unit_price
            // 备份批量操作的数据
            this.teamCourseTotalList[o.course_id] = this.teamCourseTotalList[
              o.course_id
            ]
              ? this.teamCourseTotalList[o.course_id]
              : {}
            this.teamCourseTotalList[o.course_id].team_unit_price = +this
              .teamOperationObject.team_unit_price
          }
        })
      } else if (type === 'personal') {
        forEach(this.personalCourseList, o => {
          if (o.courseChecked) {
            o.personal_unit_price = +this.personalOperationObject
              .personal_unit_price
            // 备份批量操作的数据
            this.personalCourseTotalList[o.course_id] = this
              .personalCourseTotalList[o.course_id]
              ? this.personalCourseTotalList[o.course_id]
              : {}
            this.personalCourseTotalList[
              o.course_id
            ].personal_unit_price = +this.personalOperationObject
              .personal_unit_price
          }
        })
      }
    },
    coachAllOperationOk() {
      // 批量设置
      forEach(this.personalCourseList, value => {
        if (value.courseChecked) {
          value.coachGradeList = cloneDeep(this.personalAllOperationCoachList)
          this.personalCourseTotalList[value.course_id] = this
            .personalCourseTotalList[value.course_id]
            ? this.personalCourseTotalList[value.course_id]
            : {}
          this.personalCourseTotalList[value.course_id].list = cloneDeep(
            this.personalAllOperationCoachList
          )
        }
      })
      // 缓存选择的教练等级
      this.personalCoachListHistory = []
      forEach(this.personalCourseList, i => {
        this.personalCoachListHistory.push(i.coachGradeList)
      })
      // 清空
      this.personalAllOperationCoachList = []
      // 收起
      this.coachAllOperationDropdownIsShow = false
    }
  },
  created() {
    if (isEmpty(this.initInfo)) return ''
    this.packageData.is_team = !!this.initInfo.is_team
    this.packageData.is_personal = !!this.initInfo.is_personal
    this.packageData.personal_unit_price = this.initInfo.personal_unit_price
    this.packageData.team_unit_price = this.initInfo.team_unit_price
    if (this.initInfo.is_limit) {
      this.packageData.personal_times = this.initInfo.personal_times
      this.packageData.team_times = this.initInfo.team_times
    }
    this.teamCourseList = this.initInfo.team_range
    this.personalCourseList = this.initInfo.personal_range.map(item => {
      this.$set(
        item,
        'coachGradeList',
        item.coach_level.map(item => item.id)
      )
      return item
    })
    console.log('created', this.personalCourseList)
  },
  mounted() {
    this.addRangeService.getCoachList().subscribe()
  }
}
