import BizForm from './component#/form'
import AddCourseUmlimitTable from './component#/add-course-unlimit-table'
import { AddUnlimitPackageService } from './add-unlimit-package.service'
import { isEmpty } from 'lodash-es'
export default {
  name: 'AddUnlimitPackage',
  serviceInject() {
    return {
      service: AddUnlimitPackageService
    }
  },
  data() {
    return {
      rangeCourse: {},
      validateCourseRange: ''
    }
  },
  components: {
    BizForm,
    AddCourseUmlimitTable
  },
  methods: {
    onCourseRange(rangeCourse) {
      if (isEmpty(rangeCourse)) {
        this.validateCourseRange = '请选择课程范围'
        return
      } else {
        this.validateCourseRange = ''
      }
      this.rangeCourse = { ...rangeCourse }
    },
    onSubmit(form) {
      if (isEmpty(this.rangeCourse)) {
        this.validateCourseRange = '请选择课程范围'
        return
      }
      this.service.add({ ...this.rangeCourse, ...form }).subscribe(res => {
        this.$router.push({
          name: 'brand-product-package-list'
        })
      })
    },
    onSaveAndUp(form) {
      if (isEmpty(this.rangeCourse)) {
        this.validateCourseRange = '请选择课程范围'
        return
      }
      this.service.addAndUp({ ...this.rangeCourse, ...form }).subscribe(res => {
        this.$router.push({
          name: 'brand-product-package-list'
        })
      })
    }
  }
}
