import { DistributionSaleService } from './distribution-sale.service'
import { MessageService } from '@/services/message.service'
const columns = [
  {
    title: '会籍销售姓名',
    dataIndex: 'sale_name',
    key: 'sale_name'
  },
  {
    dataIndex: 'sign_bill',
    key: 'sign_bill',
    slots: { title: 'custom_title' },
    scopedSlots: { customRender: 'sign_bill' }
  }
]
export default {
  name: 'distributionAles',
  serviceInject() {
    return {
      service: DistributionSaleService,
      messageService: MessageService
    }
  },
  props: {
    memberIds: {
      type: Array,
      default() {
        return []
      }
    },
    saleId: Number
  },
  data() {
    return {
      selectedRowKeys: [],
      pagination: {
        current: 1,
        pageSize: 10
      },
      columns,
      show: false,
      list: [], // 列表数据
      chooseId: ''
    }
  },
  mounted() {
    this.service.getSaleList().subscribe(res => {
      this.list = res.list
      this.selectedRowKeys = [this.saleId]
      this.chooseId = this.saleId
    })
  },
  methods: {
    onChange(e) {
      console.log(e.target)
    },
    onSelectChange(e, itemData) {
      this.selectedRowKeys = e
      this.chooseId = itemData[0].id
    },
    handleTableChange(pagination, filters, sorter) {
      console.log(pagination)
    },
    onSearch(e) {
      this.service
        .getSaleList({
          retrieve: e
        })
        .subscribe(res => {
          this.list = res.list
        })
    },
    save() {
      console.log('sale', this.chooseId)
      this.service
        .addSale({
          member_ids: this.memberIds,
          sales_id: this.chooseId
        })
        .subscribe(res => {
          this.show = false
          this.$emit('success')
          this.messageService.success({ content: '分配成功' })
        })
    }
  },
  watch: {}
}
