import { ruleOptions } from './add-ticket.config'
import { PatternService } from '@/services/pattern.service'
import { MessageService } from '@/services/message.service'
import moment from 'moment'
import { cloneDeep } from 'lodash-es'
import { ACTIVITY_STATUS } from '../../../constants/brand/marketing'
import { UserService } from '../../../services/user.service'
export default {
  name: 'ModalAddTicket',
  bem: {
    bModal: 'modal-marketing-add-ticket'
  },
  serviceInject() {
    return {
      pattern: PatternService,
      userService: UserService,
      messageService: MessageService
    }
  },
  rxState() {
    return {
      ticketTypeList: this.userService.getOptions$('plugin.ticket_type')
    }
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      form,
      decorators,
      show: false,
      ticketType: this.ticketTypeList[1].value,
      isBulk: 1,
      // TODO: 用户人群暂未开启，前端静态填写 为全部用户 id 为 0
      crowdIdOptions: [{ label: '全部用户', value: 0 }],
      radioStyle: {
        display: 'block'
      },
      isShowSaleDatePicker: false
    }
  },
  props: {
    modalTitle: {
      type: String,
      default: '票种信息设置'
    },
    dataSource: {
      type: Array,
      default: () => []
    },
    stepForm: {
      type: Object,
      default: () => {}
    },
    formData: {
      type: Object,
      default: () => {}
    },
    index: Number,
    isSetting: {
      type: Boolean,
      default: false
    },
    ticket: {
      type: Object,
      default: () => {}
    }
  },
  computed: {
    freeTicket() {
      return this.ticketType === 1
    },
    isDisabled() {
      return !!(
        this.ticket &&
        this.ticket.ticket_id &&
        this.formData.activity_status === ACTIVITY_STATUS.PUBLISHED
      )
    }
  },
  mounted() {
    this.form.setFieldsValue({ crowd_id: 0 })
    if (!this.ticket) return
    this.$nextTick().then(() => {
      const {
        ticket_name,
        ticket_price,
        ticket_total_num,
        buy_limit_min,
        buy_limit_max,
        group_buy_min,
        buy_time_limit,
        is_group_buy,
        buy_start_time,
        buy_end_time,
        reduce_price,
        ticket_remark
      } = this.ticket
      this.ticketType = ticket_price > 0 ? 2 : 1
      const buy_time = [this.moment(buy_start_time), this.moment(buy_end_time)]
      this.form.setFieldsValue({
        ticket_name,
        ticket_price,
        ticket_total_num,
        buy_limit_min,
        buy_limit_max,
        buy_time_limit,
        ticket_remark
      })
      // 不为免费票种时才编辑回显
      console.log(reduce_price)
      if (ticket_price !== 0) {
        this.form.setFieldsValue({ group_buy_min, reduce_price })
      }
      // 初始化是否自定义时间
      this.isShowSaleDatePicker = buy_time_limit
      if (this.isShowSaleDatePicker === 1) {
        this.form.setFieldsValue({ buy_time })
      }
      this.isBulk = is_group_buy
    })
  },
  methods: {
    moment,
    formatData(values) {
      const {
        ticket_name,
        ticket_price,
        crowd_id,
        ticket_total_num,
        buy_limit_min,
        buy_limit_max,
        group_buy_min,
        reduce_price,
        buy_time_limit,
        buy_time,
        ticket_remark
      } = values
      let buy_start_time = ''
      let buy_end_time = ''
      if (buy_time_limit === 1 && buy_time) {
        buy_start_time = buy_time[0].format('YYYY-MM-DD HH:mm')
        buy_end_time = buy_time[1].format('YYYY-MM-DD HH:mm')
      }
      const form = {
        ticket_name,
        ticket_price,
        crowd_id,
        ticket_total_num,
        buy_limit_min,
        buy_limit_max,
        is_group_buy: this.isBulk,
        group_buy_min,
        reduce_price,
        buy_time_limit,
        buy_start_time,
        buy_end_time,
        ticket_remark
      }
      return form
    },
    getShowData(values) {
      const {
        ticket_name,
        ticket_price,
        crowd_id,
        ticket_total_num,
        group_buy_min,
        buy_limit_min,
        buy_limit_max,
        reduce_price,
        buy_time_limit,
        ticket_remark,
        buy_time
      } = values
      let buy_start_time = ''
      let buy_end_time = ''
      if (buy_time_limit === 1) {
        buy_start_time = buy_time[0].format('YYYY-MM-DD HH:mm')
        buy_end_time = buy_time[1].format('YYYY-MM-DD HH:mm')
      } else {
        buy_start_time = this.stepForm.start_time
        buy_end_time = this.stepForm.end_time
      }
      return {
        ticket_name,
        ticket_price,
        ticket_total_num,
        crowd_id,
        group_buy_min,
        buy_limit_min,
        buy_limit_max,
        ticket_remark,
        buy_time_limit,
        is_group_buy: this.isBulk,
        buy_start_time,
        buy_end_time,
        reduce_price
      }
    },
    onChangeGetTicketTotalNum() {
      if (this.isDisabled) return
      this.form.setFieldsValue({
        buy_limit_min: 1,
        buy_limit_max: 1
      })
      if (this.isBulk) {
        this.form.setFieldsValue({
          group_buy_min: 1,
          reduce_price: 1
        })
      }
    },
    onChangeGetTicketPrice() {
      if (this.isBulk) {
        this.form.setFieldsValue({
          group_buy_min: 1,
          reduce_price: 1
        })
      }
    },
    onSubmit() {
      this.form.validate().then(values => {
        if (values.buy_time_limit === 1 && !values.buy_time) {
          this.messageService.success({
            content: '请选择时间'
          })
          return
        }
        let showData = this.getShowData(values)
        let form = this.formatData(values)
        if (this.ticket && this.ticket.ticket_id) {
          const ticket_id = this.ticket.ticket_id
          showData = { ticket_id, ...showData }
          form = { ticket_id, ...form }
        }
        this.show = false

        // 返回表格显示数据
        this.$emit('show', {
          ticket: showData,
          index: this.index
        })

        // 提交给后台字段
        this.$emit('submit', {
          ticket: form,
          index: this.index
        })
      })
    },
    onChangeGetTicketType(e) {
      if (this.freeTicket) {
        this.form.setFieldsValue({ ticket_price: 0, is_group_buy: 0 })
        this.isBulk = 0
      }
    },
    getCurSaleTimeType(e) {
      this.isShowSaleDatePicker = e.target.value
    },
    disabledDate(current) {
      return (
        current && current.format('YYYY-MM-DD') < moment().format('YYYY-MM-DD')
      )
    }
  }
}
