import { BrandService } from './brand.service'
import { MessageService } from '@/services/message.service'
import { UserService } from '@/services/user.service'
import { PatternService } from '@/services/pattern.service'
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue'
export default {
  bem: {
    b: 'page-setting-brand'
  },
  components: {
    StImageUpload
  },
  serviceInject() {
    return {
      brandService: BrandService,
      messageService: MessageService,
      userService: UserService,
      pattern: PatternService
    }
  },
  rxState() {
    return {
      brandInfo: this.brandService.brandInfo$,
      systemInfo: this.brandService.systemInfo$,
      loading: this.brandService.loading$
    }
  },
  data() {
    return {
      imageList: []
    }
  },
  computed: {
    isEdit() {
      return this.$searchQuery.type === 'edit'
    }
  },
  created() {
    this.imageList = [this.brandInfo.image]
  },
  methods: {
    onEdit() {
      this.$router.push({
        query: {
          type: 'edit'
        }
      })
    },
    onSave() {
      if (!this.inputCheck()) {
        return
      }
      const info = this.brandInfo
      const params = {
        brand_name: info.brand_name,
        album_id: info.album_id,
        image: this.imageList[0],
        description: info.description
      }
      this.brandService.update(params).subscribe(() => {
        this.messageService.success({
          content: '保存成功'
        })
        this.$router.push({})
        this.userService.fetchStaffInfo().subscribe()
      })
    },
    onCancel() {
      this.$router.push({
        query: {}
      })
    },
    onImgChange(list) {
      this.imageList = list
    },
    inputCheck() {
      const info = this.brandInfo
      const brandName = info.brand_name
      const { pattern } = this
      if (!pattern.CN_EN_NUM_SPACE('1-20').test(brandName)) {
        this.tip('品牌名称支持20字以内的中英文和数字')
        return false
      }
      return true
    },
    tip(msg) {
      this.messageService.error({
        content: msg
      })
    }
  }
}
