export default {
  name: 'h5FormReserveinfoComponent',
  bem: {
    module: 'h5-form-reserveinfo-component'
  },
  components: {},
  props: {
    moduleDatas: {
      type: Array,
      default: function() {
        return []
      }
    }
  },
  watch: {
    moduleDatas: {
      handler(newVal) {
        if (newVal.length === 0) return
        this.list = newVal[0].content.list
        console.log(14666, this.list)
      },
      immediate: true,
      deep: true
    }
  },
  data() {
    return {
      list: [],
      canDrag: true
    }
  },
  methods: {
    onClick(index) {
      console.log('----', this.list, index)
      let displayTotal = 0
      this.list.forEach(val => {
        if (val.is_display) {
          displayTotal++
        }
      })
      if (displayTotal === 1 && this.list[index].is_display) {
        return
      }
      this.list[index].is_display = +!this.list[index].is_display
      this.changeData()
    },
    draggChange() {
      this.$emit('draggChange', this.list)
    },
    changeData() {
      this.$emit('change')
    }
  }
}
