import { StopSaleService } from './stop-sale.service'
export default {
  name: 'ModalCardShopDepositStopSale',
  bem: {
    stopSale: 'modal-card-shop-stop-sale'
  },
  serviceInject() {
    return {
      stopSaleService: StopSaleService
    }
  },
  rxState() {
    return {
      loading: this.stopSaleService.loading$,
      info: this.stopSaleService.info$
    }
  },
  props: ['id', 'cardName'],
  data() {
    return {
      form: this.$stForm.create(),
      show: false,
      reason: '',
      // 停售原因是否有填写
      reasonIsNone: true
    }
  },
  methods: {
    onReasonChange(data) {
      this.reasonIsNone = data.trim() === ''
    },
    onStop() {
      this.stopSaleService
        .setStopSale(
          {
            reason: this.reason
          },
          this.id
        )
        .subscribe(() => {
          this.show = false
          this.$emit('success')
        })
    }
  },
  mounted() {
    this.stopSaleService.getStopSaleInfo(this.id).subscribe()
  }
}
