import { ReactiveService } from './reactive.service'
import { ruleOptions } from './reactive.config'
import moment from 'moment'
import { get } from 'lodash'
import { BATCH_TYPE, BATCH_INFO } from '@/constants/common/batch-operation'
export default {
  name: 'ModalSoldCardReactive',
  serviceProviders() {
    return [ReactiveService]
  },
  serviceInject() {
    return {
      reactiveService: ReactiveService
    }
  },
  rxState() {
    return {
      list_num: this.reactiveService.list_num$,
      loading: this.reactiveService.loading$
    }
  },
  props: {
    ids: {
      type: Array,
      required: true
    },
    searchQuery: {
      type: Object
    }
  },
  mounted() {
    this.reactiveService.getTaskReactiveNum(this.searchQuery).subscribe()
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      BATCH_TYPE,
      BATCH_INFO,
      form,
      decorators,
      addDate: 0,
      show: false,
      batch_type: BATCH_TYPE.SELECTED
    }
  },
  computed: {
    expirationDate() {
      return moment()
        .add(this.addDate, 'days')
        .format('YYYY-MM-DD HH:ss')
    },
    helpText() {
      return this.list_num > this.BATCH_INFO.LIMIT_NUM
        ? this.BATCH_INFO.ERROR_TIP
        : ''
    },
    helpShow() {
      return this.list_num > this.BATCH_INFO.LIMIT_NUM
    },
    operateDataNum() {
      return this.batch_type === this.BATCH_TYPE.SELECTED
        ? this.ids.length
        : this.list_num
    }
  },
  methods: {
    moment,
    onChangeDate(value) {
      this.addDate = value
    },
    onSubmit() {
      this.form.validate().then(values => {
        values.extend_days = +values.extend_days
        this.reactiveService
          .taskReactive({
            batch_type: this.batch_type,
            sold_ids: this.ids,
            conditions: this.searchQuery,
            ...values
          })
          .subscribe(res => {
            this.$emit('success')
            this.show = false
          })
      })
    }
  }
}
