import { AddService } from '../add.service'
import { MessageService } from '@/services/message.service'
import SelectCoach from '@/views/fragments/coach/select-coach'
import { RuleConfig } from '@/constants/course/rule'

export default {
  name: 'SetShopCoach',
  serviceInject() {
    return {
      addService: AddService,
      messageService: MessageService,
      ruleConfig: RuleConfig
    }
  },
  rxState() {
    return {
      loading: this.addService.loading$
    }
  },
  components: {
    SelectCoach
  },
  props: {
    courseName: {
      type: String,
      default: ''
    },
    courseId: {
      type: Number,
      default: 0
    }
  },
  watch: {
    courseName(val) {
      this.form.setFieldsValue({
        course_name: val
      })
    },
    courseId(val) {
      this.form.setFieldsValue({
        course_id: val
      })
    }
  },
  data() {
    return {
      form: this.$stForm.create(),
      shopIds: []
    }
  },
  methods: {
    save(e) {
      e.preventDefault()
      this.form.validate().then(() => {
        const data = this.form.getFieldsValue()
        data.course_id = this.courseId
        this.addService.setShop(data).subscribe(() => {
          this.messageService.success({
            content: '提交成功'
          })
          this.$emit('goNext')
        })
      })
    },
    onSelectCoachChange(coachIds) {
      this.form.setFieldsValue({
        coach_ids: coachIds
      })
    },
    setFieldsValue() {
      const info = this.info
      this.form.setFieldsValue({
        course_name: info.course_name,
        course_category: info.course_category,
        train_aim: info.train_aim,
        duration: info.duration,
        is_online_sale: info.is_online_sale,
        price: info.price,
        effective_unit: info.effective_unit,
        image: info.image,
        description: info.description
      })
      this.fileList = [this.info.image]
    },
    getData() {
      const data = this.form.getFieldsValue()
      data.course_id = +this.$searchQuery.id
      return data
    }
  }
}
