import { ruleOptions } from './editable-number.config'
export default {
  name: 'EditableNumber',
  props: {
    value: {
      type: [String, Number],
      required: true,
      default: ''
    },
    unit: {
      type: String,
      default: ''
    },
    placeholder: {
      type: String,
      default: '请输入价格'
    },
    float: {
      type: Boolean,
      default: true
    },
    disabled: {
      type: Boolean,
      default: false
    },
    form: {
      type: Object,
      default: () => {
        return {}
      }
    }
  },
  data() {
    return {
      text: null,
      isEdit: false
    }
  },
  watch: {
    value(val) {
      this.text = val
    }
  },
  computed: {
    ruleOptions,
    decorators() {
      return this.form.addDecorators(this.ruleOptions)
    }
  },
  methods: {
    onEdit() {
      this.isEdit = true
      this.form.setFieldsValue({
        isConfirmed: false
      })
    },
    onConfirm() {
      const text = this.text || '0'
      this.form.setFieldsValue({
        isConfirmed: true
      })
      this.$emit('input', text)
      this.$emit('ok', text)
      this.isEdit = false
    },
    onBlur() {
      clearTimeout(this.timer)
      this.timer = setTimeout(() => {
        this.form.validateFields(['isConfirmed'], {
          force: true
        })
      }, 100)
    }
  }
}
