import moment from 'moment'
import ReserveIsLimit from './components#/reserveIsLimit'
import { cloneDeep, remove } from 'lodash-es'
import { AddUnlimitPackageService } from './add-unlimit-package.service'
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue'
export default {
  name: 'ShopUnlimitPackageAdd',
  serviceInject() {
    return {
      addPackageService: AddUnlimitPackageService
    }
  },
  rxState() {
    return {
      transferUnitList: this.addPackageService.transferUnitList$,
      sellTypeList: this.addPackageService.sellTypeList$,
      unitList: this.addPackageService.unitList$,
      addLoading: this.addPackageService.loading$
    }
  },
  components: {
    StImageUpload,
    ReserveIsLimit
  },
  bem: {
    add: 'page-shop-add-unlimit-package',
    basic: 'page-shop-add-basic-package'
  },
  data() {
    return {
      packageData: {
        // 课程包名称
        course_name: '',
        // 是否支持团体课 0为不支持 1为支持
        is_team: 0,
        // 团体课总节数
        team_times: undefined,
        // 团体课单价
        team_unit_price: undefined,
        // 是否支持私教课 0为不支持 1为支持
        is_personal: 0,
        // 私教课总节数
        personal_times: undefined,
        // 私教课单价
        personal_unit_price: undefined,
        // 售价
        price: undefined,
        // 售卖开始时间
        start_time: '',
        // 售卖截止时间
        end_time: '',
        // 有效时间值
        valid_time: undefined,
        // 有效时间单位 1:天 2:月 3:年
        valid_time_unit: 1,
        // 允许冻结天数
        frozen_days: undefined,
        // 是否可以转让: 0 不可以 1 可以
        is_allow_transfer: 0,
        // 转让费率
        transfer_rate: undefined,
        // 转让单位 1:百分比 2:元
        transfer_unit: 1,
        // 售卖方式
        sale_mode: [2],
        // 封面对象
        image: {
          image_id: undefined,
          image_key: ''
        },
        // 课程包介绍
        intro: '',
        // 备注
        remarks: ''
      },
      form: this.$form.createForm(this),
      start_time: null,
      end_time: null,
      endOpen: false,
      // 课程范围是否未输入过
      courseIsFirstInput: true,
      // 课程范围是否未配置
      courseIsNone: false,
      courseErrorText: '',
      // 是否未传了封面
      imageIsNone: false,
      imageErrorText: '',
      fileList: [],
      cropperModal: {}
    }
  },
  methods: {
    // 保存
    save() {
      this.course_validator()
      this.image_validator()
      this.form.validateFieldsAndScroll((err, values) => {
        if (!err && !this.courseIsNone && !this.imageIsNone) {
          this.packageData.course_name = values.course_name
          this.packageData.price = values.price
          this.packageData.valid_time = values.valid_time
          this.packageData.support_num = values.support_num
          this.packageData.frozen_days = values.frozen_days
          this.packageData.transfer_rate = this.packageData.is_allow_transfer
            ? +values.transfer_rate
            : undefined
          this.packageData.start_time =
            typeof this.start_time === 'string'
              ? this.start_time
              : `${this.start_time.format('YYYY-MM-DD')}`
          this.packageData.end_time =
            typeof this.end_time === 'string'
              ? this.end_time
              : `${this.end_time.format('YYYY-MM-DD')}`
          // 约课限制 5.22 start
          this.packageData.reserve_is_limit = values.reserve_is_limit
          this.packageData.reserve_limit_day = values.reserve_limit_day
          this.packageData.reserve_limit_times = +values.reserve_limit_times
          // 约课限制 5.22 end
          this.addPackageService.add(this.packageData).subscribe(res => {
            this.$router.push({
              path: '/shop/product/course/manage/package/list'
            })
          })
        }
      })
    },
    // 保存并上架
    onsale() {
      this.course_validator()
      this.image_validator()
      this.form.validateFieldsAndScroll((err, values) => {
        if (!err && !this.courseIsNone && !this.imageIsNone) {
          this.packageData.course_name = values.course_name
          this.packageData.price = values.price
          this.packageData.valid_time = values.valid_time
          this.packageData.support_num = values.support_num
          this.packageData.frozen_days = values.frozen_days
          this.packageData.transfer_rate = this.packageData.is_allow_transfer
            ? +values.transfer_rate
            : undefined
          // 约课限制 5.22 start
          this.packageData.reserve_is_limit = values.reserve_is_limit
          this.packageData.reserve_limit_day = values.reserve_limit_day
          this.packageData.reserve_limit_times = +values.reserve_limit_times
          // 约课限制 5.22 end
          this.packageData.start_time = `${this.start_time.format(
            'YYYY-MM-DD'
          )}`
          this.packageData.end_time = `${this.end_time.format('YYYY-MM-DD')}`
          this.addPackageService
            .addAndOnsale(this.packageData)
            .subscribe(res => {
              this.$router.push({
                path: '/shop/product/course/manage/package/list'
              })
            })
        }
      })
    },
    // course validatorFn
    course_validator() {
      this.courseIsFirstInput = false
      let teamIsOk = this.packageData.is_team ? !!this.team_total : true
      let personalIsOk = this.packageData.is_personal
        ? !!this.personal_total
        : true
      if (
        teamIsOk &&
        personalIsOk &&
        !(!this.packageData.is_team && !this.packageData.is_personal)
      ) {
        // 校验通过
        this.courseIsNone = false
        this.courseErrorText = ''
      } else {
        // 校验未通过
        this.courseIsNone = true
        this.courseErrorText = '请输入上课范围'
      }
    },
    // image validatorFn
    image_validator() {
      if (this.packageData.image.image_key !== '') {
        // 校验通过
        this.imageIsNone = false
        this.imageErrorText = ''
      } else {
        // 校验未通过
        this.imageIsNone = true
        this.imageErrorText = '请上传封面'
      }
    },
    // checkboxChange
    teamCheckboxChange(e) {
      this.packageData.is_team = +e.target.checked
      this.courseIsFirstInput = false
    },
    personalCheckboxChange(e) {
      this.packageData.is_personal = +e.target.checked
      this.courseIsFirstInput = false
    },
    // 转让
    transfer(e) {
      this.packageData.is_allow_transfer = +e.target.checked
      // 重置转让费用的校验
      this.packageData.transfer_rate = undefined
      this.form.resetFields(['transfer_rate'])
    },
    fileChange(data) {
      if (data.length) {
        // 上传
        this.packageData.image.image_id = data[0].image_id
        this.packageData.image.image_key = data[0].image_key
        this.imageIsNone = false
        this.imageErrorText = ''
      } else {
        // 删除
        this.packageData.image.image_id = null
        this.packageData.image.image_key = ''
        this.imageIsNone = true
        this.imageErrorText = '请上传封面'
      }
    },
    transferUnitChange() {
      this.packageData.transfer_rate = undefined
      this.form.setFieldsValue({
        transfer_rate: undefined
      })
    },
    // start_time validatorFn
    start_time_validator(rule, value, callback) {
      if (!value) {
        // eslint-disable-next-line
        callback('请选择开始售卖时间')
      } else {
        // eslint-disable-next-line
        callback()
      }
    },
    // end_time validatorFn
    end_time_validator(rule, value, callback) {
      if (!value) {
        // eslint-disable-next-line
        callback('请选择结束售卖时间')
      } else {
        // eslint-disable-next-line
        callback()
      }
    },
    // 售卖时间-start
    start_time_change(data) {
      this.start_time = cloneDeep(data)
    },
    handleStartOpenChange(open) {
      if (!open) {
        this.endOpen = true
      }
    },
    disabledStartDate(startValue) {
      const endValue = this.end_time
      if (!endValue) {
        // 结束时间未选择
        return (
          startValue.valueOf() <
          moment()
            .startOf('day')
            .valueOf()
        )
      }
      let start =
        endValue.valueOf() >
        moment()
          .add(30, 'y')
          .valueOf()
          ? moment(endValue)
              .subtract(30, 'y')
              .valueOf()
          : moment()
              .startOf('day')
              .valueOf()
      return (
        startValue.valueOf() < start ||
        startValue.valueOf() > moment(endValue).valueOf()
      )
    },
    // 售卖时间-end
    end_time_change(data) {
      this.end_time = cloneDeep(data)
    },
    handleEndOpenChange(open) {
      this.endOpen = open
    },
    disabledEndDate(endValue) {
      const startValue = this.start_time
      if (!startValue) {
        // 开始时间未选择
        return (
          endValue.valueOf() <
          moment()
            .startOf('day')
            .valueOf()
        )
      }
      return (
        endValue.valueOf() >=
          moment(startValue)
            .add(30, 'y')
            .valueOf() ||
        endValue.valueOf() < moment(startValue).valueOf() ||
        endValue.valueOf() <
          moment()
            .startOf('day')
            .valueOf()
      )
    },
    // moment
    moment
  },
  computed: {
    // 团课小计
    team_total() {
      if (+this.packageData.team_times && +this.packageData.team_unit_price) {
        return (
          (this.packageData.team_times *
            (this.packageData.team_unit_price * 10)) /
          10
        )
      } else {
        return 0
      }
    },
    // 私教小计
    personal_total() {
      if (
        +this.packageData.personal_times &&
        +this.packageData.personal_unit_price
      ) {
        return (
          (this.packageData.personal_times *
            (this.packageData.personal_unit_price * 10)) /
          10
        )
      } else {
        return 0
      }
    },
    // 总价
    all_total() {
      let teamTotal = this.packageData.is_team ? this.team_total : 0
      let personalTotal = this.packageData.is_personal ? this.personal_total : 0
      return teamTotal + personalTotal
    }
  }
}
