import { InfoService } from './info.service'
import tableMixin from '@/mixins/table.mixin'
import {
  personalFixColumns,
  personalRangeColumns,
  teamFixColumns,
  teamRangeColumns,
  courseUnLimitColumns,
  shopColumns
} from './info.config'
import { columns } from '../../../common/export.config'
import OnSales from '@/views/biz-modals/package/onSales'
import OffSales from '@/views/biz-modals/package/offSales'
import ShopList from '@/views/biz-modals/package/shop-list'
import Synchronize from '@/views/biz-modals/package/synchronize'
import SuccessTip from '@/views/biz-modals/package/success-tip'
import { SHOP_RANGE, PRODUCT_CHANNEL } from '@/constants/course/package.ts'
export default {
  name: 'BrandPackageInfo',
  bem: {
    b: 'page-brand-package-info'
  },
  modals: {
    OnSales,
    OffSales,
    ShopList,
    Synchronize,
    SuccessTip
  },
  mixins: [tableMixin],
  data() {
    return {
      FIXATION: 3,
      UNLIMIT: 1,
      RANGE: 2,
      SHOP_RANGE,
      PRODUCT_CHANNEL
    }
  },
  serviceInject() {
    return {
      service: InfoService
    }
  },
  rxState() {
    const { info$, auth$ } = this.service
    return {
      info$,
      auth$
    }
  },
  computed: {
    btnOptions() {
      return [
        {
          text: '编辑',
          click: this.onEdit,
          if: this.auth$['brand:product:package_course|edit']
        },
        {
          text: '删除',
          click: this.deletePackage,
          if: this.auth$['brand:product:package_course|del']
        },
        {
          text: '下架',
          click: this.onDownShelf,
          if: this.auth$['brand:product:package_course|down']
        },
        {
          text: '上架',
          click: this.onUpShelf,
          if: this.auth$['brand:product:package_course|up']
        },
        {
          text: '应用至售出',
          click: this.batchOperate,
          if: this.auth$['brand:product:package_course|batch_operate']
        }
      ]
    },
    packageTypeConst() {
      return {
        RANGE: 2,
        FIXATION: 3,
        UNLIMITED: 1
      }
    },
    unlimitRange() {
      return [
        {
          course_type: '团课课程',
          price: this.info$.team_unit_price,
          count: this.info$.team_times,
          total: this.info$.team_unit_price * this.info$.team_times
        },
        {
          course_type: '私教课程',
          price: this.info$.personal_unit_price,
          count: this.info$.personal_times,
          total: this.info$.personal_unit_price * this.info$.personal_times
        }
      ]
    },
    personalFixColumns,
    personalRangeColumns,
    teamFixColumns,
    teamRangeColumns,
    shopColumns,
    courseUnLimitColumns,
    packageType() {
      return +this.$route.query.packageType
    },
    teamColumns() {
      if (this.packageType === this.packageTypeConst.RANGE) {
        return this.teamRangeColumns
      }
      if (this.packageType === this.packageTypeConst.FIXATION) {
        return this.teamFixColumns
      }
      return this.teamFixColumns
    },
    personalColumns() {
      if (this.packageType === this.packageTypeConst.RANGE) {
        return this.personalRangeColumns
      }
      if (this.packageType === this.packageTypeConst.FIXATION) {
        return this.personalFixColumns
      }
      return this.personalFixColumns
    },
    isUnLimit() {
      // TODO
      return +this.packageType === this.packageTypeConst.UNLIMITED
    },
    salesDate() {
      return `${this.info$.start_time} ~ ${this.info$.end_time}`
    },
    title() {
      return this.info$.course_name
    }
  },
  methods: {
    onUpShelf() {
      let id = this.info$.package_course_id
      let channel = this.info$.publish_channel
      this.$modalRouter.push({
        name: 'on-sales',
        props: {
          id,
          channel: channel === PRODUCT_CHANNEL.SHOP ? 'shop' : 'brand'
        },
        on: {
          success: () => {
            this.$router.reload()
          }
        }
      })
    },
    onDownShelf() {
      let id = this.info$.package_course_id
      this.$modalRouter.push({
        name: 'off-sales',
        props: {
          id
        },
        on: {
          success: () => {
            this.$router.reload()
          }
        }
      })
    },
    batchOperate() {
      let id = this.info$.package_course_id
      this.$modalRouter.push({
        name: 'Synchronize',
        on: {
          success: () => {
            this.$confirm({
              title: '提示',
              content: `请再次确认是否同步，同步后会覆盖售出课程包，将会有2～5s无法正常使用`,
              onOk: () => {
                this.listService.setSyncSold(id).subscribe(res => {
                  this.$modalRouter.push({
                    name: 'success-tip'
                  })
                  this.$router.reload()
                })
              },
              onCancel() {}
            })
          }
        }
      })
    },
    deletePackage() {
      let id = this.info$.package_course_id
      let name = this.info$.course_name
      this.$confirm({
        title: '提示',
        content: ` 删除后不影响已购买的用户，一旦删除则无法恢复，确认删除${name}？`,
        onOk: () => {
          this.listService.deletePackage(id).subscribe(res => {
            this.$router.reload()
          })
        },
        onCancel() {}
      })
    },
    onEdit() {
      let id = this.info$.package_course_id
      let type = this.packageType
      let editList = {
        [this.FIXATION]: 'brand-product-package-edit-fix-package',
        [this.UNLIMIT]: 'brand-product-package-edit-unlimit-package',
        [this.RANGE]: 'brand-product-package-edit-range-package'
      }
      this.$router.push({
        name: editList[type],
        query: {
          id
        }
      })
    }
  }
}
