import mixin from './mixin'
import StImageUpload from '@/views/biz-components/st/image-upload/image-upload.vue'
export default {
  name: 'h5FormTurntableComponent',
  mixins: [mixin],
  bem: {
    module: 'h5-form-turntable-component'
  },
  components: {
    StImageUpload
  },
  data() {
    return {
      idKeyName: 'activity_id',
      modalConfig: {
        title: '选择大转盘活动',
        max: this.maxNum
      }
    }
  },
  methods: {
    adaptor(selectedRows) {
      return selectedRows.map(item => {
        return {
          activity_id: item.activity_id,
          activity_name: item.activity_name,
          image: item.image
        }
      })
    }
  }
}
