import moment from 'moment'
import { MemberChangeService } from './member-change.service.ts'
import AddCardMember from '@/views/biz-components/add-card-member/add-card-member'
import { get } from 'lodash'
export default {
  name: 'ModalSoldCourseMemberCHange',
  bem: {
    extend: 'modal-sold-course-extend'
  },
  serviceInject() {
    return {
      service: MemberChangeService
    }
  },
  rxState() {
    const { packageInfo$, loading$ } = this.service
    return { packageInfo$, loading$ }
  },
  components: {
    AddCardMember
  },
  props: ['id'],
  data() {
    return {
      date: void 0,
      list: [],
      show: false
    }
  },
  created() {
    this.service.getMemberInfo(this.id).subscribe(res => {
      this.list = this.packageInfo$.course_members
    })
  },
  methods: {
    moment,
    onChangeDate(value) {
      this.date = value
    },
    onSubmit() {
      const course_members = this.list.map(item => {
        item.id = item.id || 0
        if (item.is_minors === 1) {
          item.mobile = ''
        }
        delete item.isEdit
        return item
      })
      this.service
        .setMember({
          id: this.id,
          course_members
        })
        .subscribe(res => {
          this.$emit('success')
          this.show = false
        })
    }
  }
}
