export default {
  name: 'modalStoreOrderTip',
  bem: {
    tip: 'modal-store-order-tip'
  },
  data() {
    return {
      show: false
    }
  },
  props: {
    type: String,
    message: String
  },
  computed: {
    /**
     * 支付成功后展示查看核销订单按钮
     */
    isShowVerificationBtn() {
      return this.type === 'pay'
    }
  },
  methods: {
    viewOrder() {
      this.show = false
      this.$emit('success', {
        type: 'ViewOrder'
      })
    },
    printOrder() {
      this.show = false
      this.$emit('success', {
        type: 'PrintOrder'
      })
    },
    goPay(e) {
      this.show = false
      this.$emit('success', {
        type: 'Pay'
      })
    },
    // 取消
    onCancel() {
      this.show = false
      this.$emit('success', {
        type: 'cancel'
      })
    },
    jumpToPageVerification() {
      this.show = false
      this.$router.push({
        name: 'shop-store-order-list-verification'
      })
    }
  }
}
