export default {
  props: {
    /**
     * @example
     * [{name:'08:00之前',value:128},{name:'08:00-12:00',value:132}]
     */
    data: {
      type: Array,
      default: () => []
    }
  },
  computed: {
    maxValue() {
      return Math.max(...this.data.map(item => +item.value))
    }
  },
  methods: {
    makeProgressWidth(item) {
      if (this.maxValue <= 0) {
        return 0
      }
      return (item.value / this.maxValue) * 100
    },
    progressStyle(item) {
      return {
        width: `${this.makeProgressWidth(item)}%`
      }
    }
  }
}
