export default {
  name: 'StBtnActions',
  props: {
    options: {
      type: Array,
      default: () => [],
      required: true
    }
  },
  bem: {
    b: 'st-btn-actions'
  },
  computed: {
    computedUseableOptions() {
      return this.options.filter(item => {
        if (!('if' in item)) {
          return true
        }
        return !!item.if
      })
    },
    hasMore() {
      return this.computedUseableOptions.length > 2
    },
    outer() {
      return this.computedUseableOptions.slice(0, 2)
    },
    inner() {
      return this.computedUseableOptions.slice(2)
    }
  },
  methods: {
    shouldShow(item) {
      if (!('if' in item)) {
        return true
      }
      return !!item.if
    }
  }
}
