import shopSelect from '@/views/biz-components/shop-select'
import StTotal from '@/views/biz-components/st/total/total.vue'
import StTotalItem from '@/views/biz-components/st/total/total-item.vue'
import StDesc from '@/views/biz-components/st/desc/desc.vue'
import BrandStatDetailListModal from '@/views/biz-modals/stat/order-detail'
import BrandStatDailyListModal from '@/views/biz-modals/stat/daily-detail'

import moment from 'moment'
import { ProductService } from './product.service'
import tableMixin from '@/mixins/table.mixin'
import {
  columns,
  totalColumns,
  dailyTotalColumns,
  countDesc
} from './product.config'
export default {
  name: 'BrandStatRevenueProduct',
  mixins: [tableMixin],
  serviceInject() {
    return {
      productService: ProductService
    }
  },
  modals: {
    BrandStatDetailListModal,
    BrandStatDailyListModal
  },
  rxState() {
    console.log(this.productService)
    const {
      list$,
      page$,
      total$,
      auth$,
      dataToday$,
      updateTime$,
      productType$,
      loading$
    } = this.productService
    return {
      list: list$,
      page: page$,
      total: total$,
      auth: auth$,
      dataToday: dataToday$,
      updateTime: updateTime$,
      productType: productType$,
      loading: loading$
    }
  },
  bem: {
    b: 'page-brand-stat-revenue-product',
    bHeader: 'page-brand-stat-header'
  },
  data() {
    return {
      dataTodayShop: -1
    }
  },
  created() {
    this.getDataToday()
  },
  computed: {
    columns,
    countDesc,
    totalColumns,
    dailyTotalColumns
  },
  methods: {
    moment,
    // TODO:
    onChangeDataDays(event) {
      this.$searchQuery.day = event.recently_day || undefined
      this.$searchQuery.start_date = event.start_date || undefined
      this.$searchQuery.end_date = event.end_date || undefined
      this.onSearch()
    },
    getDataToday() {
      return this.productService
        .getDataToday({ shop_id: this.dataTodayShop })
        .subscribe()
    },
    refresh() {
      return this.productService.getDataRefresh()
    },
    dailyRefresh() {
      return this.productService.getDataToday({
        shop_id: this.dataTodayShop,
        is_refresh: 1
      })
    }
  },
  components: {
    shopSelect,
    StTotal,
    StTotalItem,
    StDesc
  }
}
