import moment from 'moment'
import { get } from 'lodash-es'
export default {
  name: 'StCustomRadioGroup',
  bem: {
    b: 'st-recent-radio-group'
  },
  props: {
    days: {
      type: Array,
      default: () => {
        return [
          {
            id: 1,
            name: '今日'
          },
          {
            id: 2,
            name: '昨日'
          },
          {
            id: 3,
            name: '明日'
          }
        ]
      }
    },
    value: {
      type: Object,
      default: () => {
        return {
          recent: undefined,
          start_time: undefined,
          end_time: undefined
        }
      }
    }
  },
  data() {
    return {
      recent: 1,
      dateRange: []
    }
  },
  watch: {
    recent(newValue) {
      if (newValue) {
        this.$emit('change', {
          recent: newValue,
          start_time: undefined,
          end_time: undefined
        })
        this.dateRange = []
      }
    }
  },
  mounted() {
    if (this.value.start_date && this.value.end_date) {
      this.recent = false
      this.dateRange = [
        moment(this.value.start_date),
        moment(this.value.end_date)
      ]
    } else {
      this.dateRange = []
    }
    if (this.value.recent) this.recent = this.value.recent
  },
  methods: {
    moment,
    recentChange(date, dateString) {
      this.recent = undefined
      this.$emit('change', {
        recent: undefined,
        start_time: date[0],
        end_time: date[1]
      })
    },
    disabledDate(current) {
      if (this.$attrs.disabledDate) {
        return this.$attrs.disabledDate(current)
      }
      return current && current > moment().add(6, 'months')
    }
  }
}
