import { cloneDeep } from 'lodash-es'
export default {
  name: 'H5ModuleComponent',
  bem: {
    module: 'h5-module-component'
  },
  props: {
    moduleInfo: {
      type: Object,
      default: () => {}
    },
    isActive: {
      type: Boolean,
      default: false
    },
    // 没有slot时使用不可更改模板
    isNoChange: {
      type: Boolean,
      default: false
    },
    // 是否有插槽
    hasSlot: {
      type: Boolean,
      default: false
    }
  },
  data() {
    return {
      // 是否正在重命名
      renaming: false,
      // 使用不可更改模板
      hasChange: false,
      historyNmae: '',
      canDrag: true
    }
  },
  watch: {
    isActive: function(newVal) {
      !newVal && this.renameSave()
    },
    isNoChange: {
      handler(newVal) {
        if (newVal) {
          this.hasChange = true
        }
      },
      deep: true,
      immediate: true
    }
  },
  mounted() {
    this.historyNmae = this.moduleInfo.module_name
  },
  methods: {
    change() {
      this.$emit('change', this.moduleInfo)
    },
    editName() {
      this.renaming = true
    },
    renameClose() {
      this.renaming = false
      this.moduleInfo.module_name = this.historyNmae
    },
    renameSave() {
      this.renaming = false
    },
    hideModule() {
      this.moduleInfo.module_is_display = +!this.moduleInfo.module_is_display
      this.change()
    },
    deleteModule() {
      this.moduleInfo.module_is_delete = 1
      this.$emit('delete', this.moduleInfo)
    },
    mouseEmit(flag) {
      this.$emit('mouse', flag)
    }
  }
}
