import { MessageService } from '@/services/message.service'
import { OpenService } from './open.service'
import { ruleOptions } from './open.config'
import { CABINET_REASONS } from '@/constants/reception/cabinet'

export default {
  serviceInject() {
    return {
      messageService: MessageService,
      openService: OpenService
    }
  },
  rxState() {
    return {
      loading: this.openService.loading$,
      unlockReasons: this.openService.unlockReasons$
    }
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      CABINET_REASONS,
      form,
      decorators,
      show: false,
      isShowReason: false
    }
  },
  computed: {
    userInfo() {
      return `${this.userName}(${this.userMobile})`
    }
  },
  props: {
    id: {
      type: [Number, String],
      default: 0
    },
    userName: {
      type: String,
      default: ''
    },
    userMobile: {
      type: [Number, String]
    }
  },
  methods: {
    changeReasons(event) {
      this.isShowReason = event
    },
    onSubmit(e) {
      e.preventDefault()
      this.form.validate().then(values => {
        this.openService.openCabinet(this.id, values).subscribe(() => {
          this.messageService.success({
            content: '开柜成功！'
          })
          this.show = false
          this.$emit('success')
        })
      })
    },
    onCancel() {
      this.show = false
    }
  }
}
