import draggable from 'vuedraggable'
import { MessageService } from '@/services/message.service'
import { ListService } from './list.service'
import CourtAdd from '@/views/biz-modals/court/add'
import CourtEdit from '@/views/biz-modals/court/edit'
import { AREA_TYPE } from '@/constants/setting/court'
export default {
  serviceInject() {
    return {
      listService: ListService,
      messageService: MessageService
    }
  },
  rxState() {
    return {
      list: this.listService.list$,
      auth: this.listService.auth$
    }
  },
  modals: {
    CourtAdd,
    CourtEdit
  },
  components: {
    draggable
  },
  bem: {
    bPage: 'page-shop-setting-court-list',
    bItem: 'court-item',
    bAdd: 'court-add'
  },
  data() {
    return {
      AREA_TYPE
    }
  },
  methods: {
    onAddCourt() {
      const that = this
      this.$modalRouter.push({
        name: 'court-add',
        on: {
          change() {
            that.onListChange()
          }
        }
      })
    },
    onUpdateCourt(id) {
      const that = this
      this.$modalRouter.push({
        name: 'court-edit',
        props: {
          id
        },
        on: {
          change() {
            that.onListChange()
          }
        }
      })
    },
    onListChange() {
      this.$router.push({
        query: {}
      })
    },
    onDel(id) {
      this.listService.del(id).subscribe(this.onDelSuccessed)
    },
    onDelSuccessed() {
      this.messageService.success({
        content: '删除成功'
      })
      this.onListChange()
    },
    onAreaSortChange() {
      this.listService.sort(this.list).subscribe()
    }
  }
}
