import { ExtendDayService } from './extend-day.service'
import { ruleOptions } from './extend-day.config'
import { BATCH_TYPE, BATCH_INFO } from '@/constants/common/batch-operation'
export default {
  name: 'ModalSoldCardExtendDay',
  serviceProviders() {
    return [ExtendDayService]
  },
  serviceInject() {
    return {
      extendDayService: ExtendDayService
    }
  },
  rxState() {
    return {
      list_num: this.extendDayService.list_num$,
      loading: this.extendDayService.loading$
    }
  },
  props: {
    ids: {
      type: Array,
      required: true
    },
    searchQuery: {
      type: Object
    }
  },
  mounted() {
    this.extendDayService.getRenewalCount(this.searchQuery).subscribe()
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      BATCH_TYPE,
      BATCH_INFO,
      form,
      decorators,
      show: false,
      batch_type: BATCH_TYPE.SELECTED
    }
  },
  computed: {
    helpText() {
      return this.list_num > this.BATCH_INFO.LIMIT_NUM
        ? this.BATCH_INFO.ERROR_TIP
        : ''
    },
    helpShow() {
      return this.list_num > this.BATCH_INFO.LIMIT_NUM
    },
    operateDataNum() {
      return this.batch_type === this.BATCH_TYPE.SELECTED
        ? this.ids.length
        : this.list_num
    }
  },
  methods: {
    onSubmit() {
      this.form.validate().then(values => {
        values.extend_days = +values.extend_days
        this.extendDayService
          .taskExtendDays({
            batch_type: this.batch_type,
            sold_ids: this.ids,
            conditions: this.searchQuery,
            ...values
          })
          .subscribe(res => {
            this.$emit('success')
            this.show = false
          })
      })
    }
  }
}
