import { ReserveIsLimitService } from './reserve-is-limit.service'
export default {
  name: 'ReserveIsLimitFormItem',
  serviceInject() {
    return {
      reserveIsLimitService: ReserveIsLimitService
    }
  },
  rxState() {
    const { reserveLimitDay$, reserveIsLimit$ } = this.reserveIsLimitService
    return {
      reserveLimitDay$,
      reserveIsLimit$
    }
  },
  data() {
    return {
      reserveIsLimit: 0
    }
  },
  props: {
    reserveIsLimitInfo: {
      type: Object,
      default: () => {
        return {
          reserve_is_limit: 0,
          reserve_limit_day: 1,
          reserve_limit_times: 3
        }
      }
    }
  },
  watch: {
    'reserveIsLimitInfo.reserve_is_limit'(newValue, oldValue) {
      this.reserveIsLimit = newValue
    }
  },
  methods: {
    onChangeReserveIsliimt(event) {
      this.reserveIsLimit = event.target.value
    }
  }
}
