import { NoticeService } from './notice.service'
import { thsMember, thsShop } from './notice.config'
import MemberNoticeItem from './components#/member-notice-item'
import ShopNoticeItem from './components#/shop-notice-item'
import {
  NOTIFY_TYPES,
  NOTIFY_MEMBER_SUB_TYPE,
  NOTIFY_SHOP_SUB_TYPE
} from '@/constants/setting/sms'
const pageName = 'page-setting-sms-notice'

export default {
  name: 'NoticeConfig',
  bem: {
    bPage: pageName,
    bItem: `${pageName}-item`
  },
  serviceInject() {
    return {
      noticeService: NoticeService
    }
  },
  data() {
    return {
      NOTIFY_TYPES,
      NOTIFY_MEMBER_SUB_TYPE,
      NOTIFY_SHOP_SUB_TYPE
    }
  },
  rxState() {
    const {
      memberList$,
      roleList$,
      shopList$,
      consumeType$,
      entranceType$,
      notifyTimeHour$,
      notifyRule$
    } = this.noticeService
    return {
      memberList$,
      roleList$,
      shopList$,
      consumeType$,
      entranceType$,
      notifyTimeHour$,
      notifyRule$
    }
  },
  computed: {
    thsMember,
    thsShop,
    consumeTypeString() {
      return this.consumeType$.map(item => item.label)
    },
    entranceTypeString() {
      return this.entranceType$.map(item => item.label)
    }
  },

  methods: {
    onSearch() {},
    save(para) {
      this.putNotice(para)
    },
    getNoticeList() {
      return this.noticeService.getNoticeList().subscribe()
    },
    putNotice(para) {
      return this.noticeService.putNotice({ ...para }).subscribe(res => {
        this.getNoticeList()
      })
    }
  },
  components: {
    MemberNoticeItem,
    ShopNoticeItem
  }
}
