import shopSelect from '@/views/biz-components/shop-select'
import StaffSelect from '@/views/biz-components/staff-select'
import StTotal from '@/views/biz-components/st/total/total.vue'
import StTotalItem from '@/views/biz-components/st/total/total-item.vue'
import BrandStatTeamReserveModal from '@/views/biz-modals/stat/team-reserve'

import { CourseService } from './course.service'
import tableMixin from '@/mixins/table.mixin'
import { columns, totalColumns } from './course.config'
import moment from 'moment'

export default {
  name: 'BrandCoachTeamCourse',
  mixins: [tableMixin],
  serviceInject() {
    return {
      service: CourseService
    }
  },
  rxState() {
    const {
      list$,
      page$,
      total$,
      auth$,
      loading$,
      categoryList$,
      courseList$
    } = this.service
    return {
      list: list$,
      page: page$,
      total: total$,
      auth: auth$,
      categoryList: categoryList$,
      courseList: courseList$,
      loading: loading$
    }
  },
  modals: {
    BrandStatTeamReserveModal
  },
  bem: {
    b: 'page-brand-coach-team-course',
    bHeader: 'page-brand-stat-header'
  },
  watch: {
    courseList: {
      deep: true,
      handler() {
        !this.courseList.find(
          item => item.id === this.$searchQuery.course_id
        ) &&
          this.$router.push({
            query: {
              ...this.finalSearchQuery,
              course_id: -1
            }
          })
      }
    }
  },
  data() {
    return {
      columns,
      totalColumns,
      coursePage: {
        current_page: 1,
        size: 20
      }
    }
  },
  created() {
    this.service
      .getCourseList(
        {
          type: 2,
          category_id: this.$searchQuery.category_id,
          ...this.coursePage
        },
        true
      )
      .subscribe()
    this.service.getCategoryList().subscribe()
  },
  computed: {
    value() {
      let day = this.$searchQuery.start_date ? 0 : this.$searchQuery.day
      return {
        day,
        start_date: this.$searchQuery.start_date,
        end_date: this.$searchQuery.end_date
      }
    }
  },
  methods: {
    moment,
    onChangeTableDays(event) {
      this.$searchQuery.day = event.recently_day || undefined
      this.$searchQuery.start_date = event.start_date || undefined
      this.$searchQuery.end_date = event.end_date || undefined
      this.onSearch()
    },
    onStaffSelectRefresh(isreload = false, query = {}) {
      this.coursePage.current_page = isreload
        ? 1
        : this.coursePage.current_page + 1
      return this.service
        .getCourseList(
          {
            type: 2,
            ...query,
            category_id: this.$searchQuery.category_id,
            ...this.coursePage
          },
          isreload
        )
        .subscribe()
    }
  },
  components: {
    shopSelect,
    StTotal,
    StTotalItem
  }
}
