import { OssService } from '@/services/oss.service'
import { MessageService } from '@/services/message.service'
export default {
  data() {
    return {
      loading: false,
      imageUrl: '',
      key: ''
    }
  },
  serviceInject() {
    return {
      oss: OssService,
      messageService: MessageService
    }
  },
  methods: {
    upload(data) {
      this.loading = true
      this.imageUrl = ''
      this.oss
        .put({
          file: data.file
        })
        .subscribe({
          next: val => {
            this.key = val.fileKey
            this.messageService.success({ content: `success: ${val}` })
            this.imageUrl = `http://styd-saas-test.oss-cn-shanghai.aliyuncs.com/${val.fileKey}`
            this.loading = false
          },
          error: val => {
            this.messageService.error({ content: `Error ${val.message}` })
            this.loading = false
          }
        })
    }
  }
}
