import { OffSalesService } from './off-sales.service'
import { ruleOptions } from './off-sales.config'
export default {
  name: 'PackageOffSales',
  bem: {
    bModal: 'modal-package-off-sales'
  },
  serviceInject() {
    return {
      service: OffSalesService
    }
  },
  serviceProviders() {
    return [OffSalesService]
  },
  rxState() {
    const { info$, shopOptions$ } = this.service
    return {
      info$,
      shopOptions$
    }
  },
  props: {
    id: [String, Number]
  },
  data() {
    const form = this.$stForm.create()
    const decorators = form.decorators(ruleOptions)
    return {
      form,
      decorators,
      show: false
    }
  },
  methods: {
    onOffsale() {
      this.form.validate().then(values => {
        this.service.setDownShelf({ id: this.id, ...values }).subscribe(res => {
          this.$emit('success')
          this.show = false
        })
      })
    }
  },
  created() {
    this.service.init({ id: this.id }).subscribe(res => {
      console.log('==========', res)
    })
  },
  mounted() {
    this.service.getShopList(this.id).subscribe(res => {
      this.$nextTick().then(() => {
        this.form.setFieldsValue({
          shop_ids: this.shopOptions$.map(item => item.value)
        })
      })
    })
  }
}
